CREATE DEFINER=`Buidcou1`@`%` FUNCTION `UDF_Split`(inputString VARCHAR(4000), delimiter CHAR(1)) RETURNS text CHARSET utf8mb4
    DETERMINISTIC
BEGIN
    DECLARE pos INT;
    DECLARE result TEXT DEFAULT '';

    -- Append delimiter to process the last element
    SET inputString = CONCAT(inputString, delimiter);
    
    -- Loop until all elements are extracted
    WHILE LOCATE(delimiter, inputString) > 0 DO
        SET pos = LOCATE(delimiter, inputString);
        SET result = CONCAT(result, SUBSTRING_INDEX(inputString, delimiter, 1), '\n');

        -- Remove processed part
        SET inputString = SUBSTRING(inputString FROM pos + 1);
    END WHILE;
    
    RETURN result;
END ;;
