CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `GetTickets`(IN P_UserId INT)
BEGIN
    DECLARE desgId INT DEFAULT 0;  -- Default value to prevent NULL issues
    DECLARE sqlQuery TEXT;

    -- Get designation ID for the userbjjh
    SELECT INTDESIGNATIONID INTO desgId FROM M_POR_USER WHERE INTUSERID = P_UserId;

    -- Ensure NULL values do not cause syntax errors
    IF desgId IS NULL THEN 
        SET desgId = 0; 
    END IF;

    -- Start constructing the SQL query
    SET @sqlQuery = '
    SELECT 
        VRD.ticketId,
        VRD.ticketCode,
        (SELECT IssueType FROM M_IssueType_Master WHERE issueTypeId = VRD.issueTypeId AND deletedFlag = 0) AS IssueType,
        VRD.issueTypeId,
        VRD.moduleId,
        VRD.subModuleId,
        VRD.description,
        (SELECT moduleName FROM M_Module_Master MM WHERE MM.moduleId = VRD.moduleId) AS moduleName,
        CASE 
            WHEN VRD.subModuleId IS NULL OR VRD.subModuleId = '''' THEN 
                (SELECT moduleName FROM M_Module_Master MM WHERE MM.moduleId = VRD.moduleId)
            ELSE 
                (SELECT subModuleName FROM M_SubModule_Master SM WHERE SM.subModuleId = VRD.subModuleId)
        END AS subModuleName,
        VRD.issueDocPath,
        DATE_FORMAT(VRD.createdOn, ''%d-%b-%Y'') AS createdOn,
        CASE 
            WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) IS NULL THEN ''0''
            WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 1 THEN ''1''
            WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 2 THEN ''2''
            WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 3 THEN ''3''
            WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 4 THEN ''4''
            ELSE ''Other Status''
        END AS TicketStatus,
        IFNULL((SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1), 0) AS statusId
    FROM T_VENDOR_RAISETICKET_ADD VRD 
    LEFT JOIN T_VENDOR_RAISETICKET_RESPONSE TRR ON VRD.ticketId = TRR.ticketId
    WHERE VRD.deletedFlag = 0 ';

    -- Apply conditions dynamically
    IF desgId NOT IN (8, 9) THEN
        SET @sqlQuery = CONCAT(@sqlQuery, ' AND VRD.createdBy = ', P_UserId);
    END IF;

    IF desgId = 8 THEN
        SET @sqlQuery = CONCAT(@sqlQuery, ' AND TRR.statusId NOT IN (1,2) ');
    END IF;

    IF desgId = 9 THEN
        SET @sqlQuery = CONCAT(@sqlQuery, ' AND TRR.statusId IN (4) ');
    END IF;

    -- Append ORDER BY clause
    SET @sqlQuery = CONCAT(@sqlQuery, ' ORDER BY VRD.createdOn DESC;');

    -- Execute the dynamic SQL
    PREPARE stmt FROM @sqlQuery;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;

END ;;
