    IN P_ImagePath VARCHAR(100),
    IN P_remarks VARCHAR(500),
    IN P_ImageDescription TEXT,
    IN P_Status INT,
    IN P_ActiveFrom DATE,
    IN P_ActiveTo DATE,
    IN P_CreatedBy INT,
    IN P_UpdatedBy INT,
    IN P_PendingWithUser INT,
    OUT P_ResultMessage NVARCHAR(200)
    
)
BEGIN
DECLARE P_PlinkName varchar(500);
    DECLARE ErrorMessage VARCHAR(4000);
    Declare P_DesignationID INT;
    DECLARE L_PendingWithUser INT;
    DECLARE L_PendingWithDesignation INT;
    DECLARE L_HighestIntlevelUserid INT;
    DECLARE L_HighestIntlevelDesignationid INT;
    
    -- CREATE Operation
    IF P_Action = 'CREATE' THEN
      BEGIN
      SELECT intuserid ,intdesigid
        INTO L_PendingWithUser,L_PendingWithDesignation
        FROM BUIDCO_devdb.Approval_Config 
        -- WHERE FormName = P_FormName AND intlevel = 1
        WHERE intDeleteflag=0 and  intlevel = 1 and FormName = P_FormName
        LIMIT 1;
    
        INSERT INTO tbl_Banner (
            Title, 
            ImagePath, 
            ImageDescription, 
            ActiveFrom, 
            ActiveTo, 
            CreatedOn, 
            CreatedBy, 
            IsDeleted, 
            IsActive, 
            Status,PendingWithUser,PendingWithDesignation
        ) 
        VALUES (
            P_Title, 
            P_ImagePath, 
            P_ImageDescription, 
            P_ActiveFrom, 
            P_ActiveTo, 
            NOW(), 
            P_CreatedBy, 
            0, 
            0, 
            0,
            L_PendingWithUser,L_PendingWithDesignation
        );
        SET P_ResultMessage = 'Record inserted successfully.';
        END;
    -- GETBYID Operation
    ELSEIF P_Action = 'GETBYID' THEN
    BEGIN
        SELECT 
            Id, 
            Title, 
            ImagePath, 
            ImageDescription, 
            Status, 
            DATE_FORMAT(ActiveFrom, '%Y-%m-%d') AS ActiveFrom, 
            DATE_FORMAT(ActiveTo, '%Y-%m-%d') AS ActiveTo
        FROM tbl_Banner
        WHERE Id = P_Id AND IsDeleted = 0;
        SET P_ResultMessage = 'Record fetched successfully.';
        END;
  
    ELSEIF P_Action = 'GETALL' THEN
    BEGIN
     if(P_PendingWithUser!=1 ) then
     BEGIN
        SELECT 
            Id, 
            PendingWithUser,
            PendingWithDesignation
            Title, 
            ImagePath, 
            ImageDescription, 
            Status, 
            Approve_Status,
            DATE_FORMAT(ActiveFrom, '%d %M %Y') AS ActiveFrom, 
            DATE_FORMAT(ActiveTo, '%d %M %Y') AS ActiveTo
        FROM tbl_Banner
        WHERE 
        IsDeleted = 0
        AND
        Status = 0 
        And 
        PendingWithUser=P_PendingWithUser ##BY user
        OR
        PendingWithDesignation=(SELECT INTDESIGNATIONID from M_POR_USER Where 
       INTUSERID=P_PendingWithUser)##BY Designation
        ;
        SET P_ResultMessage = 'All records fetched successfully.';
      
      END;
      else
        BEGIN
           SELECT 
            Id, PendingWithUser,
            PendingWithDesignation,
            Title, 
            Approve_Status,
            ImagePath, 
            ImageDescription, 
            Status, 
            DATE_FORMAT(ActiveFrom, '%d %M %Y') AS ActiveFrom, 
            DATE_FORMAT(ActiveTo, '%d %M %Y') AS ActiveTo
        FROM tbl_Banner
        WHERE IsDeleted = 0 AND Status = 0 ;
        SET P_ResultMessage = 'All records fetched successfully.';
        END;
        end if;
        END;
    -- UPDATE Operation
    
    ELSEIF P_Action = 'UPDATE' THEN
    
   BEGIN
        IF P_ImagePath IS NULL THEN
        BEGIN
            UPDATE tbl_Banner
            SET
                Title = P_Title,
                ImageDescription = P_ImageDescription,
                ActiveFrom = P_ActiveFrom,
                ActiveTo = P_ActiveTo,
                UpdatedOn = NOW(),
                UpdatedBy = P_UpdatedBy
            WHERE Id = P_Id AND IsDeleted = 0;
            END;
        ELSE
        BEGIN
            UPDATE tbl_Banner
            
            SET
                Title = P_Title,
                ImagePath = P_ImagePath,
                ImageDescription = P_ImageDescription,
                ActiveFrom = P_ActiveFrom,
                ActiveTo = P_ActiveTo,
                UpdatedOn = NOW(),
                UpdatedBy = P_UpdatedBy
            WHERE Id = P_Id AND IsDeleted = 0;
            END;
        END IF;
        SET P_ResultMessage = 'Record updated successfully.';
        END;
    -- DELETE Operation
    ELSEIF P_Action = 'DELETE' THEN
    BEGIN
        UPDATE tbl_Banner
        SET
            IsDeleted = 1,
            DeletedOn = NOW(),
            DeletedBy = P_UpdatedBy
        WHERE Id = P_Id;
        SET P_ResultMessage = 'Record marked as deleted.';
		END;
		
        ELSEIF P_Action = 'ApproveBanner' THEN
        BEGIN
        
        SET P_DesignationID=(SELECT INTDESIGNATIONID from M_POR_USER Where 
       INTUSERID=P_PendingWithUser);
        BEGIN
        
			SELECT intuserid,intdesigid into L_HighestIntlevelUserid,
            L_HighestIntlevelDesignationid
			FROM BUIDCO_devdb.Approval_Config
			
             where Approval_Config.intDeleteflag=0 and 
             Approval_Config.FormName COLLATE utf8mb4_0900_ai_ci = P_FormName COLLATE utf8mb4_0900_ai_ci
			AND intlevel = (SELECT MAX(intlevel) FROM BUIDCO_devdb.Approval_Config
		     
             where Approval_Config.intDeleteflag=0 
             and Approval_Config.FormName 
             COLLATE utf8mb4_0900_ai_ci = P_FormName COLLATE utf8mb4_0900_ai_ci);



				SELECT intuserid, intdesigid into L_PendingWithUser,
                L_PendingWithDesignation
				FROM BUIDCO_devdb.Approval_Config 
				
                      where Approval_Config.intDeleteflag=0 and 
                      Approval_Config.FormName COLLATE utf8mb4_0900_ai_ci 
                      = P_FormName COLLATE utf8mb4_0900_ai_ci
				  AND intlevel = (
					  SELECT intlevel + 1
					  FROM BUIDCO_devdb.Approval_Config 
					  
                      where Approval_Config.intDeleteflag=0 and 
                      Approval_Config.FormName COLLATE utf8mb4_0900_ai_ci 
                      = P_FormName COLLATE utf8mb4_0900_ai_ci
					  AND intuserid = P_PendingWithUser
                      OR
                      intdesigid=P_DesignationID##BY Designation
					  LIMIT 1
				  )
				;
        if
        (
        (L_HighestIntlevelUserid=P_UpdatedBy) 
        OR 
        (
        L_HighestIntlevelDesignationid=(SELECT INTDESIGNATIONID 
        from
        M_POR_USER Where 
       INTUSERID=P_UpdatedBy)
       )
       ) 
        then
        BEGIN
        insert into T_Approve_Remark_log
        (bannerid,
        remarks,
        userid,
        intdesigid,
        CreatedBy)
        
        values(P_Id,
        P_remarks,
        P_UpdatedBy,
        (SELECT INTDESIGNATIONID 
        from
        M_POR_USER Where 
       INTUSERID=P_UpdatedBy),
       P_UpdatedBy);
        
        UPDATE tbl_Banner
			SET
				PendingWithUser=0,
                PendingWithDesignation=0,
                Approve_Status=1,
				UpdatedOn= NOW(),
				UpdatedBy= P_UpdatedBy,
                remarks=P_remarks,
                plink=P_FormName
               
			WHERE Id = P_Id;
            END;
        else
           BEGIN
		 insert into T_Approve_Remark_log(
         bannerid,
         remarks,
         userid,
         intdesigid,
         CreatedBy)
        values(P_Id,
        P_remarks,
        P_UpdatedBy,
        (SELECT INTDESIGNATIONID 
        from
        M_POR_USER Where 
       INTUSERID=P_UpdatedBy),
        P_UpdatedBy);
            
			UPDATE tbl_Banner
            SET
				PendingWithUser=L_PendingWithUser,
                PendingWithDesignation=L_PendingWithDesignation,
				UpdatedOn= NOW(),
				UpdatedBy= P_UpdatedBy,
                remarks=P_remarks,
                plink=P_FormName
			WHERE Id = P_Id;
            END;
          end if;
           SET P_ResultMessage = 'Approved Successfully';
  END;
  END;
 
 -- REVERTBANNER Operation
    ELSEIF P_Action = 'REVERTBANNER' THEN
    BEGIN
        -- Check if already approved
        IF (SELECT Approve_Status FROM tbl_Banner WHERE Id = P_Id) = 1 THEN
        BEGIN
            -- Get previous Pending User & Designation
            SELECT PendingWithUser, PendingWithDesignation
            INTO L_PendingWithUser, L_PendingWithDesignation
            FROM T_Approve_Remark_log
            WHERE bannerid = P_Id 
            ORDER BY id DESC
            LIMIT 1;

            -- Insert revert log
            INSERT INTO T_Approve_Remark_log (
                bannerid, remarks, userid, intdesigid, CreatedBy
            ) VALUES (
                P_Id, P_remarks, P_UpdatedBy,
                (SELECT INTDESIGNATIONID FROM M_POR_USER WHERE INTUSERID = P_UpdatedBy),
                P_UpdatedBy
            );

            -- Update tbl_Banner to revert approval
            UPDATE tbl_Banner
            SET PendingWithUser = L_PendingWithUser,
                PendingWithDesignation = L_PendingWithDesignation,
                Approve_Status = 0,  -- Revert status
                UpdatedOn = NOW(),
                UpdatedBy = P_UpdatedBy,
                remarks = P_remarks
            WHERE Id = P_Id;

            SET P_ResultMessage = 'Revert action successful.';
        END;
        ELSE
        BEGIN
            SET P_ResultMessage = 'Revert failed: Banner is not approved.';
        END;
        END IF;
    END;
 
 -- Invalid Action
    ELSE
    BEGIN
        SET ErrorMessage = 'Invalid Action Parameter.';
        SET P_ResultMessage = ErrorMessage;
        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = ErrorMessage;
        END;
    END IF;
    
END ;;
