BEGIN
IF (P_Action='V')Then 		
		SELECT  F.INTFUNCTIONID ,F.VCHFUNCTION ,B.NVCHBUTTON,B.VCHURL,B.NVCHDESCRIPTION,B.INTBUTTONID
		FROM M_ADM_BUTTON B
		inner join M_ADM_FUNCTION F on B.INTFUNCTIONID=F.INTFUNCTIONID where B.BITSTATUS=P_intStatus ;
