		set BITSTATUS=1,INTUPDATEDBY=P_INTUPDATEDBY,DTMUPDATEDON=curdate()
		where  INTBUTTONID = P_INTBUTTONID;
		Commit	; 
		SELECT * from M_JK_SUCCESSMESSAGE where successid=5;             	
	COMMIT;
    ELSEIF(P_Action='IA')Then   
			Update M_ADM_BUTTON
			set BITSTATUS=0,INTUPDATEDBY=P_INTUPDATEDBY,DTMUPDATEDON=curdate()
			where  INTBUTTONID = P_INTBUTTONID;
			Commit;			
			SELECT * from M_JK_SUCCESSMESSAGE where successid=5;             	
			COMMIT;

	ELSEIF (P_Action='E')Then  ####View  Particular data Id wise
		SELECT INTBUTTONID,INTFUNCTIONID,NVCHBUTTON,VCHURL,NVCHDESCRIPTION,INTSORTNUM,VCHBUTTON1,VCHBUTTON2,VCHBUTTON3
		FROM M_ADM_BUTTON   where  INTBUTTONID= P_INTBUTTONID;   
	END IF;   
END ;;
