CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_Common_Provider`(
    IN P_Action CHAR(30),
    IN P_INT_ID INT
    #IN P_VCH_PMT VARCHAR(10)
)
BEGIN
	IF (P_Action = 'A') # For State
    THEN 
      select levelDetailId,levelName from M_Adm_LevelDetails where parentid=0 and levelid=1 and deletedflag=0 order by levelname;

    ELSEIF (P_Action = 'B') # District Name
    THEN
      select levelDetailId,levelName from M_Adm_LevelDetails where parentid=1 and levelid=2 and deletedflag=0 order by levelname;

    ELSEIF (P_Action = 'C')# Block Name 
    THEN
		select levelDetailId,levelName from M_Adm_LevelDetails where parentid=P_INT_ID and levelid=3 and deletedflag=0 order by levelname;
	ELSEIF (P_Action = 'D') # WARD/GP Name
    THEN
        select levelDetailId,levelName from M_Adm_LevelDetails where parentid=P_INT_ID and levelid=4 and deletedflag=0 order by levelname;
   
    ELSEIF (P_Action = 'E') # Project Category
    THEN
        SELECT categoryId, categoryName  
        FROM M_Project_Category_Master WHERE  deletedFlag=0 ORDER BY categoryName;
        
	ELSEIF (P_Action = 'F') # Project subcategory
    THEN
        SELECT subCategoryId,subCategoryName  
        FROM M_Project_Subcategory_Master WHERE  deletedFlag=0 and categoryId=P_INT_ID
        ORDER BY subCategoryName;
        
	ELSEIF(P_Action = 'G') THEN # Get Milestone by categoryId
        SELECT milestoneId, milestoneName  
        FROM M_MileStone_Master WHERE  deletedFlag=0 and   categoryId=P_INT_ID
        ORDER BY milestoneName;
        
	ELSEIF(P_Action = 'H') THEN # Get Proposal Details
        SELECT proposalId,letterNo,subject as proposalSubject FROM BUIDCO_devdb.M_Proposal_Master where deletedFlag=0 and proposalId not in
		( select proposalId from T_PROJECT_MASTER where deletedFlag=0) and proposalStatus=4 and statusId=3
        ORDER BY proposalId;
	ELSEIF(P_Action = 'I') THEN # Get Project
		Select ProjectId,ProjectName From T_PROJECT_MASTER where deletedflag=0 order by ProjectName; 
    ELSEIF(P_Action = 'J') THEN # Get director
		Select VCHUSERNAME as directorname,INTUSERID as directorid from M_POR_USER where INTDESIGNATIONID=3 order by vchusername;
    ELSEIF(P_Action='K') THEN # Get Project Name  
         SELECT ProjectId,projectName FROM T_PROJECT_MASTER WHERE deletedFlag=0 order by projectName;
    ELSEIF(P_Action='L') THEN # status
	   SELECT statusId, statusName from M_Application_Status_Master where FIND_IN_SET(statusId,'3,2,4')
	   ORDER BY  FIELD(statusId, 3, 2, 4);
	ELSEIF(P_Action='M') THEN # Financial Year
		Select fyId,fyName from M_Financial_Year where deletedflag=0 order by fyName ;
	ELSEIF(P_Action='N') THEN # # Letter Number
		Select proposalId,LetterNo from M_Proposal_Master where deletedflag=0 order by LetterNo;
	ELSEIF(P_Action='O') THEN # # client
		Select clientId,clientName from M_Client_Master where deletedFlag=0	order by clientName;
	ELSEIF(P_Action='P') THEN # # consultant
		Select consultant_id as ID,consultantName from M_Consultant where deletedFlag=0	order by consultantName;
	ELSEIF(P_Action='Q') THEN # # project type
		Select typeId,projectType from M_Project_Type_Master where deletedFlag=0 order by projectType;
	ELSEIF(P_Action='R') THEN # # Scheme List
		Select schemeId Id,SchemeName Value from M_Scheme_Master where deletedFlag=0 order by SchemeName;
  
   END IF;
    
END ;;
