CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_CPKG_1_USERLOGIN`(
    IN P_VCHUSERNAME VARCHAR(100),
    IN P_VCHPASSWORD VARCHAR(100),
    OUT Output VARCHAR(500),
    IN P_Action CHAR(20)
)
BEGIN
    IF P_Action = 'LOGIN' THEN
       SELECT INTUSERID,VCHUSERNAME,VCHFULLNAME,INTDESIGNATIONID,ROLEID,(select NVCHDESIGNAME from M_ADM_DESIGNATION WHERE INTDESIGID=INTDESIGNATIONID) as DESIGNATIONNAME FROM M_POR_USER 
       -- SELECT * from M_POR_USER 
WHERE VCHUSERNAME = P_VCHUSERNAME COLLATE utf8mb4_unicode_ci 
AND VCHPASSWORD = P_VCHPASSWORD COLLATE utf8mb4_unicode_ci;

        -- Optionally, set an output value
        IF ROW_COUNT() > 0 THEN
            SET Output = 'Login Successful';
        ELSE
            SET Output = 'Invalid Credentials';
        END IF;
    
    ELSEIF P_Action = 'Chngpassword' THEN
        IF EXISTS (SELECT 1 FROM M_POR_USER WHERE VCHUSERNAME = P_VCHUSERNAME AND VCHPASSWORD = P_VCHPASSWORD) THEN
            UPDATE CPKG_1_USER 
            SET VCHPASSWORD = P_VCHPASSWORD 
            WHERE VCHUSERNAME = P_VCHUSERNAME;
            SET Output = 1;
        ELSE
            SET Output = 2;
        END IF;
    END IF;
END ;;
