				,vchportletfile = mvarvchPortletFile
                ,bitnewtab=mvarvchNewTab
				,bitStatus=1
                ,BITNEWTAB=mvarvchNewTab
				,intupdatedby = mvarintCreatedBy
				,dtmupdatedon = curdate()
				WHERE intfunctionid = mvarintFunctionId; 

                 
                     
                    SELECT  * FROM M_JK_SUCCESSMESSAGE WHERE successid = 2;    #Added by SmrutiRanjanBiswal(26/03/2020)

