		#	ELSE				
				update M_ADM_FUNCTION
				SET bitStatus = 0
				,intupdatedby = mvarintCreatedBy
				,dtmupdatedon = curdate()
				WHERE intfunctionid =mvarintFunctionId
				AND bitStatus = 1;
                
                
                                           SELECT  * FROM M_JK_SUCCESSMESSAGE WHERE successid = 3;    #Added by SmrutiRanjanBiswal(31/03/2020)
			end if;	            
		#end if;

		if (mvarchrActionCode='T') # For Active
		then        
			UPDATE M_ADM_FUNCTION 
			SET bitStatus = 1
			,intupdatedby = mvarintCreatedBy
			,dtmupdatedon = curdate()
			WHERE intfunctionid =mvarintFunctionId 
			AND bitStatus = 0;

            
                
