CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_FUNCTION_VIEW`(
in p_chrActionCode		CHAR(10),
in p_intFunctionId		int,
in p_intFlag			int,
in p_vchFunName		varchar(100),
in p_vchSearchText		varchar(100)
)
BEGIN

 IF (upper(p_chrActionCode)='V') THEN
    
        IF (p_vchFunName is null OR p_vchFunName='')THEN
        	SET @row_number = 0;   
        
            SELECT  (@row_number:=@row_number + 1) AS RowNo ,intFunctionId 
            ,vchFunction
            ,vchFileName
            ,vchDescription
            ,(
                case 
                    when upper(vchAction1)='ADD' then 'Y' 
                    when vchAction1 is null then 'N'  
                    when vchAction1='' then 'N' 
                    ELSE vchAction1 
                end
            ) as vchAction1 
            ,(
                case 
                    when upper(vchAction2)='VIEW' then 'Y' 
                    when vchAction2 is null then 'N'  
                    when vchAction2='' then 'N' 
                    ELSE vchAction2 
                end
            ) as vchAction2
            ,(
                case 
                    when upper(vchAction3)='MANAGE' then 'Y' 
                    when vchAction3 is null then 'N'  
                    when vchAction3='' then 'N' 
                    ELSE vchAction3 
                end
            ) as vchAction3 
            ,bitMailSend						 
            ,bitFreebees
            ,vchportletfile
			,BITNEWTAB,
            (
                case 
                    when BITNEWTAB=1 then 'Y' 
                    when BITNEWTAB is null then 'N'  
                    when BITNEWTAB=0 then 'N' 
                    ELSE BITNEWTAB 
                end
            ) as vchAction4 
            FROM M_ADM_FUNCTION 
            WHERE bitStatus = p_intFlag
            ORDER BY vchFunction ASC;
        ELSE
			SELECT 
            (@row_number:=@row_number + 1) AS RowNo 
            ,intFunctionId 
            ,vchFunction
            ,vchFileName
            ,vchDescription
            ,(
                case 
                    when upper(vchAction1)='ADD' then 'Y' 
                    when vchAction1 is null then 'N'  
                    when vchAction1='' then 'N' 
                    ELSE vchAction1 
                end
            ) as vchAction1 
            ,(
                case 
                    when upper(vchAction2)='VIEW' then 'Y' 
                    when vchAction2 is null then 'N'  
                    when vchAction2='' then 'N' 
                    ELSE vchAction2 
                end
            ) as vchAction2
            ,(
                case 
                    when upper(vchAction3)='MANAGE' then 'Y' 
                    when vchAction3 is null then 'N'  
                    when vchAction3='' then 'N' 
                    ELSE vchAction3 
                end
            ) as vchAction3 
            ,bitMailSend						 
            ,bitFreebees
            ,vchportletfile
            ,BITNEWTAB,
            (
                case 
                    when BITNEWTAB=1 then 'Y' 
                    when BITNEWTAB is null then 'N'  
                    when BITNEWTAB=0 then 'N' 
                    ELSE BITNEWTAB 
                end
            ) as vchAction4 
            FROM M_ADM_FUNCTION 
            WHERE bitStatus = p_intFlag 
            and upper(vchFunction) like concat('%',concat(upper(p_vchFunName),'%'))
            ORDER BY vchFunction ASC;
        END IF;			
        
   ELSE IF (upper(p_chrActionCode)='E') THEN
   SET @row_number = 0;   
      SELECT 
            (@row_number:=@row_number + 1) AS RowNo 
         ,intfunctionid
        ,vchFunction
        ,vchFileName
        ,vchDescription
        ,(
            case 
                when vchAction1='Add' then 'Y' 
                when vchAction1 is null then 'N'  
                when vchAction1='' then 'N' 
                else vchAction1 
            end
        ) as vchAction1 
        ,(
            case 
                when vchAction2='View' then 'Y' 
                when vchAction2 is null then 'N'  
                when vchAction2='' then 'N' 
                else vchAction2 
            end
        ) as vchAction2
        ,(
            case 
                when vchAction3='Manage' then 'Y' 
                when vchAction3 is null then 'N'  
                when vchAction3='' then 'N' 
                else vchAction3 
            end
        ) as vchAction3 
        ,bitMailSend							  
        ,bitFreebees
        ,vchportletfile
        ,BITNEWTAB
        FROM M_ADM_FUNCTION 
        WHERE bitStatus=1 
        AND intfunctionid = p_intFunctionId
         ORDER BY vchFunction ASC;
   ELSE IF (upper(p_chrActionCode)='F') 
    THEN
         SELECT intfunctionid as FunctionId
        ,vchFunction
        ,vchFileName 
        ,vchDescription 
        ,(
            case 
                when vchAction1='Add' THEN 'Y' 
                when vchAction1 is null THEN 'N'  
                when vchAction1='' THEN 'N' 
                ELSE vchAction1 
            end
        ) as vchAction1 
        ,(
            case 
                when vchAction2='View' THEN 'Y' 
                when vchAction2 is null THEN 'N'  
                when vchAction2='' THEN 'N' 
                ELSE vchAction2 
            end
        ) as vchAction2
        ,(
            case 
                when vchAction3='Manage' THEN 'Y' 
                when vchAction3 is null THEN 'N'  
                when vchAction3='' THEN 'N' 
                ELSE vchAction3 
            end
        ) as vchAction3 
        ,bitMailSend 				  							
        ,bitFreebees 
        ,vchportletfile 
        ,BITNEWTAB
        FROM M_ADM_FUNCTION 
        WHERE bitStatus=1 
        AND intfunctionid = p_intFunctionId;
        
    ELSE IF (upper(p_chrActionCode)='G') 
    THEN
         SELECT intfunctionid as FunctionId
        ,vchFunction
        ,vchfilename 
        ,vchDescription  
        ,(
            case 
                when vchAction1='Add' THEN 'Y' 
                when vchAction1 is null THEN 'N'  
                when vchAction1='' THEN 'N' 
                ELSE vchAction1 
            end
        ) as vchAction1 
        ,(
            case 
                when vchAction2='View' THEN 'Y' 
                when vchAction2 is null THEN 'N'  
                when vchAction2='' THEN 'N' 
                ELSE vchAction2 
            end
        ) as vchAction2
        ,(
            case 
                when vchAction3='Manage' THEN 'Y' 
                when vchAction3 is null THEN 'N'  
                when vchAction3='' THEN 'N' 
                ELSE vchAction3 
            end
        ) as vchAction3 
        ,bitMailSend 					  							
        ,bitFreebees 
        ,vchportletfile 
        ,BITNEWTAB
        FROM M_ADM_FUNCTION 
        WHERE bitStatus=1;	

    ELSE IF (upper(p_chrActionCode)='H') 
    THEN
         SELECT intfunctionid as FunctionId
        ,vchFunction
        ,vchFileName 
        ,vchDescription  
        ,(
            case 
                when vchAction1='Add' THEN 'Y' 
                when vchAction1 is null THEN 'N'  
                when vchAction1='' THEN 'N' 
                ELSE vchAction1 
            end
        ) as vchAdd 
        ,(
            case 
                when vchAction2='View' THEN 'Y' 
                when vchAction2 is null THEN 'N'  
                when vchAction2='' THEN 'N' 
                ELSE vchAction2 
            end
        ) as vchView
        ,(
            case 
                when vchAction3='Manage' THEN 'Y' 
                when vchAction3 is null THEN 'N'  
                when vchAction3='' THEN 'N' 
                ELSE vchAction3 
            end
        ) as vchManage 
        ,bitMailSend					  						
        ,bitFreebees
        ,vchportletfile
        ,BITNEWTAB
        FROM M_ADM_FUNCTION 
        WHERE bitStatus=1  
        and vchFunction like concat(upper(p_vchSearchText),'%'); 
    END IF;    
	END IF;	
	END IF;	
	END IF;	
	END IF;	
END ;;
