CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_LEVELMASTER_MANAGE`(
	in P_ACTION  CHAR(10),
	in P_INTLEVELID  INT ,
	in P_INTHIERARCHYID  INT,
    in P_VCHALLIAS VARCHAR(100),
	in P_NVCHLABEL VARCHAR(100),
	in P_INTPARENTID  INT,
	in P_INTCREATEDBY  INT,
	in P_INTUPDATEDBY  INT
)
BEGIN
	declare mvarintMaxID VARCHAR(50);   
    declare mvarintPositionID VARCHAR(50);  
    declare	mvarmvarMyCount VARCHAR(50); 
	declare mvarmvardupCount VARCHAR(50); 
    declare  mvarnooflevel VARCHAR(50);
    declare  mvarchknooflevel VARCHAR(50);
	declare	mvardepdencecount VARCHAR(50);
    
    IF  (P_ACTION='A')Then    #Add Data

         select count(INTLEVELID) into mvarmvarMyCount #check for duplicate entry in levelwise with Hierararchy
				from  M_ADM_LEVEL
				WHERE  INTHIERARCHYID=P_INTHIERARCHYID AND INTPARENTID=P_INTPARENTID
				AND bitStatus=1;  

        select count(INTLEVELID) into mvarmvardupCount 
				from  M_ADM_LEVEL
				WHERE UPPER(RTRIM(LTRIM(NVCHLABEL)))= UPPER(RTRIM(LTRIM(P_NVCHLABEL))) AND INTPARENTID=P_INTPARENTID 
				AND bitStatus=1; 

        select  INTNOLEVEL  into mvarnooflevel#check no of level in Hierarchy table 
				from  M_ADM_HIERARCHY
				WHERE  INTHIERARCHYID=P_INTHIERARCHYID 
				AND bitStatus=1; 

        select count(INTLEVELID) into mvarchknooflevel #check no of record in level table by hierarchy
				from  M_ADM_LEVEL
				WHERE  INTHIERARCHYID=P_INTHIERARCHYID 
				AND bitStatus=1;


			if (mvarmvardupCount>0 ) then 
							   
			SELECT * from M_JK_SUCCESSMESSAGE where successid=4;  

       ELSEIF ( mvarchknooflevel = mvarnooflevel) then 
        
			SELECT * from M_JK_SUCCESSMESSAGE where successid=8; 

      ELSEIF ( mvarmvarMyCount >0) then 
        
			SELECT * from M_JK_SUCCESSMESSAGE where successid=11;  
			ELSE						

			select max(INTLEVELID)+1 into mvarintMaxID from M_ADM_LEVEL; 
      select max(INTPOSITION)+1 into mvarintPositionID from M_ADM_LEVEL where INTHIERARCHYID=P_INTHIERARCHYID and bitstatus=1; 


		INSERT INTO M_ADM_LEVEL
			(INTLEVELID,INTHIERARCHYID,NVCHLABEL,INTPOSITION,INTPARENTID,INTCREATEDBY,INTUPDATEDBY)								   
			VALUES
			(mvarintMaxID,P_INTHIERARCHYID,P_NVCHLABEL,mvarintPositionID,P_INTPARENTID,P_INTCREATEDBY,P_INTUPDATEDBY)  ;

			 COMMIT;
			   
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=1;             	
			COMMIT;
     END IF;                        

ELSEIF(P_Action='E')Then  ##Update Data
BEGIN

	 select count(INTLEVELID) into mvarmvarMyCount 
				from  M_ADM_LEVEL
				WHERE  INTHIERARCHYID=P_INTHIERARCHYID AND INTPARENTID=P_INTPARENTID and INTLEVELID not in(P_INTLEVELID)
				AND bitStatus=1;  

        select count(INTLEVELID) into mvarmvardupCount 
				from  M_ADM_LEVEL
				WHERE UPPER(RTRIM(LTRIM(NVCHLABEL)))= UPPER(RTRIM(LTRIM(P_NVCHLABEL))) AND  INTHIERARCHYID=P_INTHIERARCHYID  and INTLEVELID not in(P_INTLEVELID)
				AND bitStatus=1; 

        select INTNOLEVEL into mvarnooflevel#check no of level in Hierarchy table 
				from  M_ADM_HIERARCHY
				WHERE  INTHIERARCHYID=P_INTHIERARCHYID 
				AND bitStatus=1; 

        select count(INTLEVELID) into mvarchknooflevel #check no of record in level table by hierarchy
				from  M_ADM_LEVEL
				WHERE  INTHIERARCHYID=P_INTHIERARCHYID 
				AND bitStatus=1;

        	select count(INTLEVELDETAILID)   into mvardepdencecount from M_ADM_LEVELDETAILS #-check for dependency record before  Edit
	where INTLEVELID=P_INTLEVELID AND bitStatus=1;



      IF mvardepdencecount >0 Then
        
			SELECT * from M_JK_SUCCESSMESSAGE where successid=7; 
      ELSEIF (mvarmvarMyCount >0 OR mvarmvardupCount>0) then 
							   
			SELECT * from M_JK_SUCCESSMESSAGE where successid=4;  
			ELSE	 					

      UPDATE 		M_ADM_LEVEL
      SET INTHIERARCHYID=P_INTHIERARCHYID,NVCHLABEL=P_NVCHLABEL,INTPARENTID=P_INTPARENTID,INTUPDATEDBY=P_INTUPDATEDBY,DTMUPDATEDON=curdate()
      WHERE INTLEVELID=P_INTLEVELID;


			 COMMIT;
			   
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=2;             	
			COMMIT;
  END IF;  

END;

ELSEIF(P_Action='I')Then  ##Mark As Inactive
BEGIN

	select count(INTLEVELDETAILID)   into mvardepdencecount from M_ADM_LEVELDETAILS #check for dependency record before  De;ete
	where INTLEVELID=P_INTLEVELID AND bitStatus=1;

if mvardepdencecount >0 then 

							   
			SELECT * from M_JK_SUCCESSMESSAGE where successid=7;  
			ELSE	

 UPDATE 		M_ADM_LEVEL
  SET  BITSTATUS=0,INTUPDATEDBY=P_INTUPDATEDBY,DTMUPDATEDON=curdate()
  WHERE INTLEVELID=P_INTLEVELID;     
Commit;

   
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=6;             	
			COMMIT;
END IF;
END;


ELSEIF(P_Action='EA')Then  ##Mark As Inactive
BEGIN


    	select count(INTLEVELDETAILID)    into mvardepdencecount from M_ADM_LEVELDETAILS #check for dependency record before  De;ete
	where INTLEVELID in(P_INTLEVELID) AND bitStatus=1;


 UPDATE 		M_ADM_LEVEL
  SET  BITSTATUS=1,INTUPDATEDBY=P_INTUPDATEDBY,DTMUPDATEDON=curdate()
  WHERE INTLEVELID=P_INTLEVELID;     
Commit;


   
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=5;             	
			COMMIT;

END;
    END IF;
END ;;
