CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_M_AA_Approval`(
   OUT P_MSG_OUT varchar(100),
IN P_Action CHAR(30),
IN P_AAId INT,
IN P_PROPOSALID INT,
IN P_AALETTERPATH VARCHAR(100),
IN P_AALETTERNO VARCHAR(50),
IN  P_AALETTERDATE DATETIME,
IN  P_AASCHEMEID INT,
# IN  P_AAAPPROVEDBY int,
#IN P_AASIGNEDDOCUMENT VARCHAR(100),
IN P_OTHERDOC VARCHAR(100),
IN P_IsMultipleProject INT,
IN P_AAPARTPROJECT TEXT, -- JSON input
IN P_CREATEDBY INT,
IN P_TotalAmount DECIMAL(18,2),
IN P_AAUploadRemarks VARCHAR(250)
)
BEGIN
	DECLARE v_error_message TEXT;
	 
	DECLARE v_error_code INT;
	Declare V_Controller varchar(100);
	DECLARE v_error_procedure VARCHAR(200) DEFAULT   'USP_M_AA_Approval';
	DECLARE i INT DEFAULT 0;
	DECLARE total INT DEFAULT 0;
		
	DECLARE jsonData JSON;
	DECLARE v_ProjectPart VARCHAR(255);
	DECLARE v_Description VARCHAR(500);
	DECLARE v_Amount VARCHAR(500);
	 
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
          GET DIAGNOSTICS CONDITION 1
           v_error_message = MESSAGE_TEXT,
           v_error_code = MYSQL_ERRNO;
               ROLLBACK;
      
        -- Capture additional debug details
       CALL usp_write_error_log(v_error_message,v_error_code,v_error_procedure,P_Action,V_Controller);
        SET P_MSG_OUT ='0';
	END;
      
           set @@sql_mode = '';  
	set P_AAPARTPROJECT = replace(P_AAPARTPROJECT,'\"','"'); 


    IF JSON_VALID(P_AAPARTPROJECT) = 1 THEN
        SET jsonData = CAST(P_AAPARTPROJECT AS JSON);
        SET total = JSON_LENGTH(jsonData); -- Count number of elements
    ELSE
        SET jsonData = '[]'; -- Default to empty array if invalid JSON
        SET total = 0;
    END IF;
    
	IF P_Action = 'A' THEN
		INSERT INTO T_PROPOSAL_AA_DETAILS(proposalId,schemeId,otherDocPath,aaRemarks,isMultipleProject,
        createdBy,createdOn,aaEstimatedAmount,deletedFlag) values
		(P_PROPOSALID,P_AASCHEMEID,P_OTHERDOC,P_AAUploadRemarks,P_IsMultipleProject
        ,P_CREATEDBY,now(),P_TotalAmount,0);
        SET @lastid=LAST_INSERT_ID();
        WHILE i < total DO
			set @@sql_mode = '';  
            SET v_ProjectPart = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].projectpart')));
            SET v_Description = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].description')));
            SET v_Amount = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].amount')));

            INSERT INTO T_Proposal_AA_Part_Details (
                aaId, projectPart,partAmount, partDesc, createdBy, createdOn
            ) VALUES (
                @lastid, v_ProjectPart,v_Amount, v_Description, P_createdBy, NOW()
            );

            SET i = i + 1; -- Move to next item
        END WHILE;
		SET P_MSG_OUT ='1';

	ELSEIF P_Action = 'B' THEN
		UPDATE T_PROPOSAL_AA_DETAILS SET 
		schemeId=P_AASCHEMEID,isMultipleProject=P_IsMultipleProject,
		aaLetterPath=P_AALETTERPATH,otherDocPath=P_OTHERDOC,aaRemarks=P_AAUploadRemarks,
		aaEstimatedAmount='567',
		UPDATEDBY=P_CREATEDBY,UPDATEDON=NOW() WHERE proposalId=P_PROPOSALID AND deletedFlag=0;
		update T_Proposal_AA_Part_Details set deletedFlag=1 where aaId=P_AAId;
		WHILE i < total DO
			set @@sql_mode = '';  
            SET v_ProjectPart = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].projectpart')));
            SET v_Description = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].description')));
            SET v_Amount = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].amount')));

            INSERT INTO T_Proposal_AA_Part_Details (
                aaId, projectPart,partAmount, partDesc, createdBy, createdOn
            ) VALUES (
                P_AAId, v_ProjectPart,v_Amount, v_Description, P_createdBy, NOW()
            );

            SET i = i + 1; -- Move to next item
        END WHILE;
		SET P_MSG_OUT ='2';
	ELSEIF P_Action = 'C' THEN
		UPDATE T_PROPOSAL_AA_DETAILS SET 
		aaLetterPath=P_AALETTERPATH,aaLetterGenDate=NOW(),
		UPDATEDBY=P_CREATEDBY,UPDATEDON=NOW() WHERE proposalId=P_PROPOSALID AND deletedFlag=0;
		SET P_MSG_OUT ='5';
	END IF;    
	COMMIT;
END ;;
