    IN P_Action CHAR(30),
    IN P_statusId INT,
    IN P_statusName VARCHAR(50),
    IN P_createdBy INT,
    IN P_createdOn VARCHAR(50),
    IN P_updatedBy INT,
    IN P_updatedOn VARCHAR(50),
    IN P_deletedFlag INT,
    OUT P_MSG_OUT VARCHAR(100)
)
BEGIN
    IF P_Action = 'I' THEN
        INSERT INTO M_Application_Status_Master (statusName, createdBy, createdOn, updatedBy, updatedOn, deletedFlag)
        VALUES (P_statusName, P_createdBy, P_createdOn, P_updatedBy, P_updatedOn, P_deletedFlag);
    
