        UPDATE M_Application_Status_Master 
        SET statusName = P_statusName,
            createdBy = P_createdBy,
            createdOn = P_createdOn,
            updatedBy = P_updatedBy,
            updatedOn = P_updatedOn,
            deletedFlag = P_deletedFlag
        WHERE statusId = P_statusId;
    
    ELSEIF P_Action = 'D' THEN
        UPDATE M_Application_Status_Master SET deletedFlag = 1 WHERE statusId = P_statusId;
    
    ELSEIF P_Action = 'V' THEN
        SELECT statusId, statusName, createdBy, createdOn, updatedBy, updatedOn, deletedFlag FROM M_Application_Status_Master WHERE deletedFlag = 0;
    
    ELSEIF P_Action = 'S' THEN
        SELECT statusId, statusName, createdBy, createdOn, updatedBy, updatedOn, deletedFlag 
        FROM M_Application_Status_Master 
        WHERE (COALESCE(P_statusId, 0) = 0 OR statusId = COALESCE(P_statusId, 0))
        AND (COALESCE(P_statusName, '') = '' OR statusName LIKE CONCAT('%', P_statusName, '%'))
        AND (COALESCE(P_createdBy, 0) = 0 OR createdBy = COALESCE(P_createdBy, 0))
        AND (COALESCE(P_updatedBy, 0) = 0 OR updatedBy = COALESCE(P_updatedBy, 0));
    
    ELSEIF P_Action = 'E' THEN
        SELECT statusId, statusName, createdBy, createdOn, updatedBy, updatedOn, deletedFlag 
        FROM M_Application_Status_Master 
        WHERE statusId = P_statusId;
    
    END IF;
