CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_M_PD_WorkOfficeMapping`(
    OUT P_MSG_OUT VARCHAR(100),
    IN P_Action CHAR(1),
    IN P_INT_ID INT,
    IN P_Int_UserId INT,
    IN P_Int_DestrictId VARCHAR(500),
    IN P_CreatedBy INT
)
BEGIN
    -- Declare all local variables
    DECLARE counter INT DEFAULT 0;
    DECLARE v_error_message TEXT;
    DECLARE v_error_code INT;
    DECLARE v_error_procedure VARCHAR(200) DEFAULT 'USP_M_PD_WorkOfficeMapping';
    DECLARE V_Controller VARCHAR(100);

    DECLARE v_district_id VARCHAR(10);
    DECLARE v_districts TEXT;
    DECLARE done INT DEFAULT FALSE;
    DECLARE v_inserted INT DEFAULT 0;
    
   
    DECLARE district_cursor CURSOR FOR 
        SELECT TRIM(SUBSTRING_INDEX(SUBSTRING_INDEX(v_districts, ',', numbers.n), ',', -1)) AS district_id
        FROM (
            SELECT 1 n UNION SELECT 2 UNION SELECT 3 UNION SELECT 4 UNION SELECT 5
            UNION SELECT 6 UNION SELECT 7 UNION SELECT 8 UNION SELECT 9 UNION SELECT 10
            UNION SELECT 11 UNION SELECT 12 UNION SELECT 13 UNION SELECT 14 UNION SELECT 15
        ) numbers
        WHERE n <= 1 + LENGTH(v_districts) - LENGTH(REPLACE(v_districts, ',', ''));
    

    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        GET DIAGNOSTICS CONDITION 1
            v_error_message = MESSAGE_TEXT,
            v_error_code = MYSQL_ERRNO;
        ROLLBACK;
        CALL usp_write_error_log(v_error_message, v_error_code, v_error_procedure, P_Action, V_Controller);
        SET P_MSG_OUT = '0';
    END;
    
   
    START TRANSACTION;
    SET P_MSG_OUT = NULL;
    
    IF P_Action = 'I' THEN

        SET v_districts = P_Int_DestrictId;
        SET done = FALSE; 
        
        OPEN district_cursor;
        read_loop: LOOP
            FETCH district_cursor INTO v_district_id;
            IF done THEN
                LEAVE read_loop;
            END IF;
            
            SET counter = (
                SELECT COUNT(*) 
                FROM T_PD_WorkOffice_Mapping 
                WHERE Int_UserId = P_Int_UserId 
                  AND Int_DestrictId = v_district_id 
                  AND Int_DeletedFlag = 0
            );
            
            IF counter = 0 THEN
                INSERT INTO T_PD_WorkOffice_Mapping 
                    (Int_UserId, Int_DestrictId, Int_CreatedBy, Vch_CreatedOn)
                VALUES (P_Int_UserId, v_district_id, P_CreatedBy, NOW());
                
                SET v_inserted = 1;
            END IF;
        END LOOP;
        CLOSE district_cursor;
        
        IF v_inserted = 1 THEN
            SET P_MSG_OUT = '1';
        ELSE
            SET P_MSG_OUT = '4'; 
        END IF;
        
    ELSEIF P_Action = 'U' THEN
        SET counter = (
            SELECT COUNT(*) 
            FROM T_PD_WorkOffice_Mapping 
            WHERE Int_UserId = P_Int_UserId 
              AND Int_DestrictId = P_Int_DestrictId 
              AND Int_DeletedFlag = 0 
              AND Int_ID != P_INT_ID
        );
        
        IF counter = 0 THEN
            UPDATE T_PD_WorkOffice_Mapping 
            SET Int_UserId = P_Int_UserId,
                Int_DestrictId = P_Int_DestrictId,
                Int_UpdatedBy = P_CreatedBy,
                Vch_UpdatedOn = NOW()
            WHERE Int_ID = P_INT_ID;
            SET P_MSG_OUT = '2';
        ELSE
            SET P_MSG_OUT = '4';
        END IF;
        
    ELSEIF P_Action = 'D' THEN
        UPDATE T_PD_WorkOffice_Mapping 
        SET Int_DeletedFlag = 1,
            Int_UpdatedBy = P_CreatedBy,
            Vch_UpdatedOn = NOW()
        WHERE Int_ID = P_INT_ID;
        SET P_MSG_OUT = '3';
        
    ELSE
        SET P_MSG_OUT = '0';
    END IF;
    
    COMMIT;
END ;;
