CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_M_ProposalSite`(
    OUT P_MSG_OUT VARCHAR(100),
    IN P_Action CHAR(30),
    IN P_Landid INT,
    IN P_proposalid INT,
    IN P_area DECIMAL(18,2),
    IN P_nocPath VARCHAR(200),
    IN P_plannedMap VARCHAR(200),
    IN P_districtId INT,
    IN P_blockId INT,
    IN P_Wardid INT,
    IN P_address VARCHAR(200),
    IN P_lattitude VARCHAR(100),
    IN P_longitude VARCHAR(100),
    IN P_khataNo VARCHAR(100),
    IN P_khesraNo VARCHAR(100),
    IN P_createdBy VARCHAR(20),
    IN P_sitedocs TEXT
)
BEGIN
	DECLARE i INT DEFAULT 0;
    DECLARE total INT DEFAULT 0;
    
    DECLARE v_docPath VARCHAR(255);
    DECLARE jsonData JSON;
    
    DECLARE v_error_message TEXT;
    DECLARE v_error_code INT;
    DECLARE V_Controller VARCHAR(100);
    DECLARE v_error_procedure VARCHAR(200) DEFAULT 'USP_M_ProposalSite';

    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        GET DIAGNOSTICS CONDITION 1 v_error_message = MESSAGE_TEXT, v_error_code = MYSQL_ERRNO;
        ROLLBACK;
        CALL usp_write_error_log(v_error_message, v_error_code, v_error_procedure, P_Action, V_Controller);
        SET P_MSG_OUT = '0';
    END;

    START TRANSACTION;
	set @@sql_mode = '';  
	set P_sitedocs = replace(P_sitedocs,'\"','"');
 
 
    IF JSON_VALID(P_sitedocs) = 1 THEN
        SET jsonData = CAST(P_sitedocs AS JSON);
        SET total = JSON_LENGTH(jsonData); -- Count number of elements
    ELSE
        SET jsonData = '[]'; -- Default to empty array if invalid JSON
        SET total = 0;
    END IF;

    IF (P_Action = 'A') THEN
		IF(P_Landid=0) THEN
			INSERT INTO `BUIDCO_devdb`.`T_Proposal_Land_Information`
			(
				`proposalId`, `area`, `nocPath`, `plannedMap`, `districtId`, `blockId`, `Wardid`, `address`,
				`lattitude`, `longitude`, `khataNo`, `khesraNo`, `createdBy`, `createdOn`, `deletedFlag`
			)
			VALUES
			(
				P_proposalid, P_area, P_nocPath, P_plannedMap, P_districtId, P_blockId, P_Wardid, P_address,
				P_lattitude, P_longitude, P_khataNo, P_khesraNo, P_createdBy, NOW(), 0
			);

			SET @maxlandid = LAST_INSERT_ID(); -- Get the last inserted landId
	 
			WHILE i < total DO
				set @@sql_mode = '';  
				SET v_docPath = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].docPath')));
				INSERT INTO T_Proposal_Land_Document(proposalId, landId, docPath, createdBy, createdOn, deletedFlag)
				VALUES (P_proposalid, @maxlandid, v_docPath, P_createdBy, NOW(), 0);

				SET i = i + 1; -- Move to next item
			END WHILE;
			
			
			
			SET P_MSG_OUT = '1';
        else
			UPDATE `BUIDCO_devdb`.`T_Proposal_Land_Information`
			SET
			`area` = P_area,
			`nocPath` = COALESCE(P_nocPath,`nocPath`),
			`plannedMap` = COALESCE(P_plannedMap,`plannedMap`),
			`districtId` = P_districtId,
			`blockId` = P_blockId,
			`Wardid` = P_Wardid,
			`address` = P_address,
			`lattitude` = P_lattitude,
			`longitude` = P_longitude,
			`khataNo` = P_khataNo,
			`khesraNo` = P_khesraNo,
			`updatedBy` = P_createdBy,
			`updatedOn` = NOW()
			WHERE `landId` = P_Landid;
            IF(total > 0) THEN
				set @ids=(select GROUP_CONCAT(id) from T_Proposal_Land_Document where landId=P_Landid);
				update T_Proposal_Land_Document set deletedFlag=1  where  FIND_IN_SET(id, @ids)> 0 and id>0;
            END IF;
            SET @maxlandid = P_Landid;
	 
			WHILE i < total DO
				set @@sql_mode = '';  
				SET v_docPath = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].docPath')));
				INSERT INTO T_Proposal_Land_Document(proposalId, landId, docPath, createdBy, createdOn, deletedFlag)
				VALUES (P_proposalid, @maxlandid, v_docPath, P_createdBy, NOW(), 0);

				SET i = i + 1; -- Move to next item
			END WHILE;
            SET P_MSG_OUT = '2';
		End if;
	ELSEIF (P_Action = 'B') THEN
		update T_Proposal_Land_Information set deletedFlag=1,updatedBy=P_createdBy,updatedOn=NOW()
		where landid=P_Landid;
		SET P_MSG_OUT = '1';
    END IF;
    COMMIT;
END ;;
