CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_M_TakeAction`(
    OUT P_MSG_OUT VARCHAR(100),
    IN P_Action CHAR(30),
    IN P_Moduleid INT,
    IN P_ProposalId INT,
    IN P_STATUS INT,
    IN P_REMARKS VARCHAR(300),
    IN P_VCHDOC VARCHAR(50),
    IN P_CREATEDBY INT
)
BEGIN
	DECLARE ProposalWorkFlowID INT DEFAULT 1;
    DECLARE TAWorkFlowID INT DEFAULT 3;
    DECLARE AAWorkFlowID INT DEFAULT 4;
    DECLARE TSWorkFlowID INT DEFAULT 5;
    DECLARE v_error_message TEXT;
    DECLARE actionTakenUserid INT;
    DECLARE v_error_code INT;
    DECLARE V_Controller VARCHAR(100);
    DECLARE P_stepId INT DEFAULT 0;  -- Added Step ID declaration
    DECLARE v_error_procedure VARCHAR(200) DEFAULT 'USP_M_TakeAction';

    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        GET DIAGNOSTICS CONDITION 1 v_error_message = MESSAGE_TEXT,
                                    v_error_code = MYSQL_ERRNO;
        ROLLBACK;
        CALL usp_write_error_log(v_error_message, v_error_code, v_error_procedure, P_Action, V_Controller);
        SET P_MSG_OUT = '0';  -- Error Message
    END;
    START TRANSACTION;

    IF P_ProposalId IS NULL OR P_Moduleid IS NULL THEN
        SET P_MSG_OUT = '0';
        ROLLBACK;
        
    END IF;
	
    -- SET @P_Proposal = P_ProposalId;
    select stepId INTO P_stepId from M_Proposal_Master where proposalId=P_ProposalId AND deletedflag = 0;
    set @ProposalStatus=P_Moduleid;
    IF P_Action = 'A' THEN
		IF P_Moduleid = 1 THEN
            IF P_STATUS = 2 THEN # Revert
				SET P_stepId=P_stepId-1;
                UPDATE M_Proposal_Master
                SET stepId=P_stepId,statusid=P_STATUS
                WHERE proposalId = P_ProposalId AND deletedflag = 0 AND proposalId > 0;
            ELSEIF P_STATUS = 3 THEN
				IF EXISTS(select 1 from Approval_Config where FormName=ProposalWorkFlowID and intlevel=P_stepId+1) THEN
					SET P_stepId=P_stepId+1;
					UPDATE M_Proposal_Master
					SET stepId=P_stepId,statusid=P_STATUS
					WHERE proposalId = P_ProposalId AND deletedflag = 0 AND proposalId > 0;
                ELSE
					SET P_stepId=1;
                    SET P_STATUS=1;
                    SET @ProposalStatus=2;
					UPDATE M_Proposal_Master
					SET stepId=P_stepId,statusid=P_STATUS,proposalStatus=@ProposalStatus
					WHERE proposalId = P_ProposalId AND deletedflag = 0 AND proposalId > 0;
				END IF;
            ELSEIF P_STATUS = 4 THEN # Reject
                UPDATE M_Proposal_Master
                SET statusid=P_STATUS
                WHERE proposalId = P_ProposalId AND deletedflag = 0 AND proposalId > 0;
            END IF;
            INSERT INTO T_Proposal_Action_Taken(
                proposalId, moduleId, 
                actionDate, statusId, stepId, actionRemarks, 
                actionDoc, createdBy
				)
				VALUES (
					P_ProposalId, @ProposalStatus, 
					NOW(), P_STATUS, P_stepId, P_REMARKS, 
					P_VCHDOC, P_CREATEDBY
				);

            SET P_MSG_OUT = '6';  -- Success Message
        END IF;
        IF P_Moduleid = 2 THEN
            IF P_STATUS = 2 THEN # Revert
				SET P_stepId=P_stepId-1;
                UPDATE M_Proposal_Master
                SET stepId = P_stepId, statusid = P_STATUS
                WHERE proposalId = P_ProposalId AND deletedflag = 0 AND proposalId > 0;

            ELSEIF P_STATUS = 3 THEN # Approve
				IF EXISTS(select 1 from Approval_Config where FormName=TAWorkFlowID and intlevel=P_stepId+1) THEN
					SET P_stepId=P_stepId+1;
					UPDATE M_Proposal_Master
