                P_VCHDOC, P_CREATEDBY
            );

            SET P_MSG_OUT = '6';  -- Success Message
        END IF;
        IF P_Moduleid = 4 THEN
            IF P_STATUS = 2 THEN # Revert
				SET P_stepId=P_stepId-1;
                UPDATE M_Proposal_Master
                SET statusid = P_STATUS,stepId=P_stepId
                WHERE proposalId = P_ProposalId AND deletedflag = 0 AND proposalId > 0;

            ELSEIF P_STATUS = 3 THEN # Approve
                IF EXISTS(select 1 from Approval_Config where FormName=TSWorkFlowID and intlevel=P_stepId+1) THEN
					SET P_stepId=P_stepId+1;
					UPDATE M_Proposal_Master
					SET stepId=P_stepId,statusid=P_STATUS
