                actionDoc, createdBy
            )
            VALUES (
                P_ProposalId, @ProposalStatus, P_STATUS, P_stepId, P_REMARKS, 
                P_VCHDOC, P_CREATEDBY
            );

            SET P_MSG_OUT = '6';  -- Success Message
        END IF;
	END IF;

    COMMIT;
