	in P_intFunctionId  INT,
	in P_intCreatedBy  INT,
	in P_intUpdatedBy  INT,
	in P_INTSLNO  INT
)
BEGIN
	declare mvarintMaxID VARCHAR(50);      
  	declare mvarintMaxSortNum VARCHAR(50);  
    declare	mvarmvarMyCount VARCHAR(50); 
    declare mvardepdencecount VARCHAR(50);
    
    IF  (P_Action='A')Then    ####Add Data

         select count(intGLinkID) into mvarmvarMyCount 
				from M_ADM_PRIMARYLINK
				WHERE P_intGLinkId= INTGLINKID    and UPPER(RTRIM(LTRIM(nvchPlinkName)))COLLATE utf8mb4_unicode_ci= UPPER(RTRIM(LTRIM(P_nvchPLinkName)))COLLATE utf8mb4_unicode_ci 
				AND bitStatus=1;   

			if mvarmvarMyCount >0 then 
							   
			SELECT * from M_JK_SUCCESSMESSAGE where successid=4;  
			ELSE						

			select max(intPLinkId)+1 into mvarintMaxID from M_ADM_PRIMARYLINK; 
			select max(intSlNo)+1 into mvarintMaxSortNum from M_ADM_PRIMARYLINK; 

		INSERT INTO M_ADM_PRIMARYLINK
			(INTPLINKID,NVCHPLINKNAME,NVCHPLINKNAMEHIN,NVCHPLINKNAMEURD,INTGLINKID,INTFUNCTIONID,INTSLNO,INTCREATEDBY,INTUPDATEDBY)						   
			VALUES
			(mvarintMaxID,P_nvchPLinkName,P_nvchPLinkNameHin,P_nvchPLinkNameUrd,P_intGLinkId,P_intFunctionId,P_INTSLNO,P_intCreatedBy,P_intUpdatedBy)  ;

			 COMMIT;
			   
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=1;             	
			COMMIT;

     END IF;                        



ELSEIF(P_Action='I')Then  ###########Mark As Inactive
BEGIN
select  count(INTACCESSID)   into mvardepdencecount 
from M_POR_LINKACCESS where INTPLINKID=P_mvarintFunctionId and bitstatus=1; 

if mvardepdencecount >0 then 

							   
			SELECT * from M_JK_SUCCESSMESSAGE where successid=7;  
			ELSE		

Update M_ADM_PRIMARYLINK
set BITSTATUS=0,INTUPDATEDBY=P_intUpdatedBy,DTMUPDATEDON=curdate()
where  INTPLINKID = P_mvarintFunctionId;
Commit;

   
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=6;             	
			COMMIT;
END IF;


END;

ELSEIF(P_Action='EA')Then  ###########Mark As active
BEGIN

Update M_ADM_PRIMARYLINK
set BITSTATUS=1,INTUPDATEDBY=P_intUpdatedBy,DTMUPDATEDON=curdate()
where  INTPLINKID = P_mvarintFunctionId;
Commit;

   
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=5;             	
			COMMIT;

END;


ELSEIF(P_Action='E')Then  ###########Mark As Inactive
BEGIN

 select count(intGLinkID) into mvarmvarMyCount 
				from M_ADM_PRIMARYLINK
				WHERE P_intGLinkId= INTGLINKID    and UPPER(RTRIM(LTRIM(nvchPlinkName)))= UPPER(RTRIM(LTRIM(P_nvchPLinkName))) 
				AND bitStatus=1 and  INTPLINKID not in( P_mvarintFunctionId);  


			if mvarmvarMyCount >0 then 
							   
			SELECT * from M_JK_SUCCESSMESSAGE where successid=4;  
			ELSE	

Update M_ADM_PRIMARYLINK


set NVCHPLINKNAME=P_nvchPLinkName,
NVCHPLINKNAMEHIN=P_nvchPLinkNameHin,
NVCHPLINKNAMEURD=P_nvchPLinkNameUrd,
INTGLINKID=P_intGLinkId,
INTFUNCTIONID=P_intFunctionId,
INTUPDATEDBY=P_intUpdatedBy,
DTMUPDATEDON=curdate()
where  INTPLINKID = P_mvarintFunctionId;
Commit;

   
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=2;             	
			COMMIT;

      END IF;

END;
