CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_PRIMARYLINK_VIEW`(
	in P_Action CHAR(10),
	in P_intPLinkId INT,
	in P_intGLinkId int,
	in P_intStatus INT,
	in P_INTPROJECTID INT 
)
BEGIN
	IF (P_Action='VF')Then  ###########View AllFUNCTION MASTER data
BEGIN
 

				SELECT INTFUNCTIONID,VCHFUNCTION FROM M_ADM_FUNCTION where BITSTATUS=1
				  order by VCHFUNCTION asc ;                                                        
END; 


ELSEIF (P_Action='V')Then  ###########View All Primary Link data
BEGIN
 
  select P.INTPLINKID,P.NVCHPLINKNAME,P.NVCHPLINKNAMEHIN,P.NVCHPLINKNAMEURD,g.NVCHGLINKNAME,f.VCHFUNCTION,P.INTSLNO ,
   (select NVCHPROJECTLINKNAME from M_ADM_PROJECTLINK where INTPROJECTLINKID=g.INTPROJECTID) as NVCHPROJECTLINKNAME
  from M_ADM_PRIMARYLINK P
  inner join M_ADM_GLOBALLINK g on P.INTGLINKID=g.INTGLINKID
  inner join M_ADM_FUNCTION f on  P.INTFUNCTIONID=f.INTFUNCTIONID
  where P.BITSTATUS=P_intStatus  order by g.NVCHGLINKNAME,P.INTSLNO asc;                                                       
END;   
ELSEIF (P_Action='VG')Then  ###########View All Primary Link data base on  Global Link
BEGIN
 

				select P.INTPLINKID,P.NVCHPLINKNAME,P.NVCHPLINKNAMEHIN,P.NVCHPLINKNAMEURD,g.NVCHGLINKNAME,f.VCHFUNCTION,P.INTSLNO ,
          (select NVCHPROJECTLINKNAME from M_ADM_PROJECTLINK where INTPROJECTLINKID=g.INTPROJECTID) as NVCHPROJECTLINKNAME
        from M_ADM_PRIMARYLINK P
  inner join M_ADM_GLOBALLINK g on P.INTGLINKID=g.INTGLINKID
  inner join M_ADM_FUNCTION f on  P.INTFUNCTIONID=f.INTFUNCTIONID
  where g.INTGLINKID=P_intGLinkId and P.BITSTATUS=1 order by P.INTSLNO asc;                                                       
END; 

ELSEIF (P_Action='S')Then  ###########Get MaxId base on Global Link
BEGIN
 

			SELECT  MAX(INTSLNO) +1  as INTSLNO FROM M_ADM_PRIMARYLINK
      where  bitStatus=1 and INTGLINKID=P_intGLinkId;

END; 

ELSEIF (P_Action='VI')Then  ###########View  Primary Link data INTPLINKID wise
BEGIN
 
				select P.INTPLINKID,P.NVCHPLINKNAME,P.NVCHPLINKNAMEHIN,P.NVCHPLINKNAMEURD,P.INTGLINKID,P.INTFUNCTIONID,P.INTSLNO,
           (select INTPROJECTID from M_ADM_GLOBALLINK where INTGLINKID = P.INTGLINKID) as INTPROJECTLINKID
        from M_ADM_PRIMARYLINK P
        where  P.INTPLINKID=P_intPLinkId ;
        END;
ELSEIF (P_Action='VP')Then  ###########View  Poject List
BEGIN
 
				select distinct P.INTPROJECTLINKID,P.NVCHPROJECTLINKNAME 
        from M_ADM_PROJECTLINK P
        inner join M_ADM_GLOBALLINK g on P.INTPROJECTLINKID=g.INTPROJECTID where P.bitStatus=1
        order by P.INTPROJECTLINKID asc;
        END;
ELSEIF (P_Action='GP')Then  ###########View All Global Link base on Project ID
BEGIN

 

			select * from M_ADM_GLOBALLINK where INTPROJECTID=P_INTPROJECTID and  bitStatus=1;

END; 


    END IF;
END ;;
