CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_PROJECT_TENDER_BIDDER_DTLS`(
IN P_bidderID INT,
IN P_tenderId INT,
IN P_projectId INT,
in P_createdBy int,
IN P_BidderDetailsList Text,
IN P_Action char(10),
OUT P_MSG_OUT VARCHAR(100)
)
BEGIN
 DECLARE v_error_message TEXT;
    DECLARE v_error_code INT;
    DECLARE v_error_procedure VARCHAR(200) DEFAULT 'USP_PROJECT_TENDER_BIDDER_DTLS';
    DECLARE V_Controller VARCHAR(100);
    DECLARE i INT DEFAULT 0;
    DECLARE total INT DEFAULT 0;
    
            DECLARE jsonData JSON;
            DECLARE v_bidderName varchar(100);
            DECLARE v_emailId varchar(100);
            DECLARE v_contactNo varchar(15);
            DECLARE v_emdNumber varchar(15);
             DECLARE v_amount decimal(18,2);
            DECLARE v_bankName varchar(100);
            DECLARE v_emdIssueDate Datetime;
             DECLARE v_emdExpiryDate Datetime;

    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        GET DIAGNOSTICS CONDITION 1 v_error_message = MESSAGE_TEXT, v_error_code = MYSQL_ERRNO;
        
        SET P_MSG_OUT = '0';
        ROLLBACK;
        CALL usp_write_error_log(v_error_message, v_error_code, v_error_procedure, P_Action, V_Controller);
    END;


    START TRANSACTION;
     set @@sql_mode = '';  
      set P_BidderDetailsList = replace(P_BidderDetailsList,'\"','"'); 


    IF JSON_VALID(P_BidderDetailsList) = 1 THEN
        SET jsonData = CAST(P_BidderDetailsList AS JSON);
        SET total = JSON_LENGTH(jsonData); -- Count number of elements
    ELSE
        SET jsonData = '[]'; -- Default to empty array if invalid JSON
        SET total = 0;
    END IF;
    IF(P_Action='A') THEN
      WHILE i < total DO
         set @@sql_mode = '';  
            SET v_bidderName = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].bidderName')));
            SET v_emailId = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].emailId')));
            SET v_contactNo = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].contactNo')));
			SET v_emdNumber = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].emdNumber')));
            SET v_amount = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].amount')));
            SET v_bankName = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].bankName')));
            SET v_emdIssueDate = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].emdIssueDate')));
            SET v_emdExpiryDate = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].emdExpiryDate')));
           

            INSERT INTO T_PROJECT_TENDER_BIDDER_DTLS (
                tenderId,projectId,bidderName,emailId,contactNo,emdNumber,amount,bankName,emdIssueDate,emdExpiryDate,createdBy, createdOn
            ) VALUES (
                 P_tenderId,P_projectId,v_bidderName,v_emailId,v_contactNo,v_emdNumber,v_amount,v_bankName,v_emdIssueDate,v_emdExpiryDate,P_createdBy, NOW()
            );

            SET i = i + 1; -- Move to next item
        END WHILE;
        SET P_MSG_OUT = 1; -- Success
   
    ELSEIF (P_Action='B') THEN
        Update T_PROJECT_TENDER_BIDDER_DTLS set updatedBy=P_createdBy,updatedOn=NOW(),deletedFlag=1 where FIND_IN_SET(tenderId, P_tenderId) > 0 and bidderID>0;
    WHILE i < total DO
         set @@sql_mode = '';  
            SET v_bidderName = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].bidderName')));
            SET v_emailId = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].emailId')));
            SET v_contactNo = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].contactNo')));
			SET v_emdNumber = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].emdNumber')));
            SET v_amount = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].amount')));
            SET v_bankName = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].bankName')));
            SET v_emdIssueDate = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].emdIssueDate')));
            SET v_emdExpiryDate = JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].emdExpiryDate')));
           

            INSERT INTO T_PROJECT_TENDER_BIDDER_DTLS (
                tenderId,projectId,bidderName,emailId,contactNo,emdNumber,amount,bankName,emdIssueDate,emdExpiryDate,createdBy, createdOn
            ) VALUES (
                 P_tenderId,P_projectId,v_bidderName,v_emailId,v_contactNo,v_emdNumber,v_amount,v_bankName,v_emdIssueDate,v_emdExpiryDate,P_createdBy, NOW()
            );

            SET i = i + 1; -- Move to next item
        END WHILE;
        SET P_MSG_OUT = '2'; -- update Success
        
           ELSEIF (P_Action='C') THEN
        Update T_PROJECT_TENDER_BIDDER_DTLS set updatedBy=P_createdBy,updatedOn=NOW(),deletedFlag=1 where FIND_IN_SET(tenderId, P_tenderId) > 0 and bidderID>0;
     set P_MSG_OUT='3'; -- delete success
   END IF;
    commit;
END ;;
