OUT P_MSG_OUT varchar(100)
)
BEGIN
DECLARE v_error_message TEXT;
DECLARE v_error_code INT;
Declare V_Controller varchar(100);
DECLARE v_error_procedure VARCHAR(200) DEFAULT   'USP_PROJECT_TENDER_TECHNICAL';
 
     DECLARE i INT DEFAULT 0;
    DECLARE total INT DEFAULT 0;
    DECLARE jsonData JSON;
    DECLARE v_bidderId INT;
    DECLARE v_technicalScore  DECIMAL(10,2);
    DECLARE v_technicalFile  varchar(250);
  
 
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
          GET DIAGNOSTICS CONDITION 1
           v_error_message = MESSAGE_TEXT,
           v_error_code = MYSQL_ERRNO;
               ROLLBACK;
      
        -- Capture additional debug details
       CALL usp_write_error_log(v_error_message,v_error_code,v_error_procedure,P_Action,V_Controller);
        SET P_MSG_OUT ='0';
      END;
            START TRANSACTION;
            
      set @@sql_mode = '';  
      set P_technicalDetailsList = replace(P_technicalDetailsList,'\"','"'); 
    -- Validate JSON Input
    IF JSON_VALID(P_technicalDetailsList) = 1 THEN
        SET jsonData = CAST(P_technicalDetailsList AS JSON);
        SET total = JSON_LENGTH(jsonData);
    ELSE
        SET jsonData = '[]';
        SET total = 0;
    END IF;
    

      
 if(P_Action='A') THEN
  IF((select COUNT(1) from T_PROJECT_TENDER_TECHNICAL where projectId=P_projectId and tenderId=P_tenderId and  deletedFlag=0)=0)
  THEN
  INSERT INTO T_PROJECT_TENDER_TECHNICAL(tenderId,projectId,technicalStatus,technicalDocPath
 ,financialDocPath,technicalDate,finanicalDate,vchBidderID,createdBy,createdOn)
 Values(P_tenderId,P_projectId,P_technicalStatus,P_technicalDocPath
 ,P_financialDocPath,P_technicalDate,P_finanicalDate,P_vchBidderID,P_createdBy,NOW());
 
 
 SET P_ID = LAST_INSERT_ID();

        WHILE i < total DO
            SET v_bidderId = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].bidderId'))), '');
            SET v_technicalScore = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].technicalScore'))), 0);
            SET v_technicalFile = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].technicalFile'))), '');
           
     

            INSERT INTO T_TENDER_TECHNICAL_BIDDER_DTLS (
                bidderId, technicalId, technicalScore, technicalFile, createdBy, createdOn 
            ) VALUES (
                v_bidderId, P_ID, v_technicalScore, v_technicalFile, P_createdBy,NOW()
            );
        
            SET i = i + 1;
        END WHILE;
 
 
   SET P_MSG_OUT ='1';
   ELSE 
       SET P_MSG_OUT ='4';
   end if;
         
   ELSEIF(P_Action='B') THEN
   IF((select COUNT(1) from T_PROJECT_TENDER_TECHNICAL where projectId!=P_projectId and tenderId=P_tenderId and  deletedFlag=0)=0)
  THEN
   UPDATE T_PROJECT_TENDER_TECHNICAL SET
   technicalStatus=IFNULL(P_technicalStatus,technicalStatus)
  ,technicalDocPath=IFNULL(P_technicalDocPath,technicalDocPath)
  ,financialDocPath=IFNULL(P_financialDocPath,financialDocPath)
  ,technicalDate=IFNULL(P_technicalDate,technicalDate)
  ,finanicalDate=IFNULL(P_finanicalDate,finanicalDate)
  ,vchBidderID=IFNULL(P_vchBidderID,vchBidderID)
  ,updatedBy=IFNULL(P_createdBy,updatedBy)
  ,updatedOn=NOW() where ID=P_ID;
  
  UPDATE T_TENDER_TECHNICAL_BIDDER_DTLS SET deletedFlag = 1,updatedBy=P_createdBy, updatedOn=NOW()
        WHERE FIND_IN_SET(technicalId, P_ID) > 0 AND Id > 0;
        
  WHILE i < total DO
            SET v_bidderId = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].bidderId'))), '');
            SET v_technicalScore = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].technicalScore'))), 0);
            SET v_technicalFile = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].technicalFile'))), '');
           
     

            INSERT INTO T_TENDER_TECHNICAL_BIDDER_DTLS (
                bidderId, technicalId, technicalScore, technicalFile, createdBy, createdOn 
            ) VALUES (
                v_bidderId, P_ID, v_technicalScore, v_technicalFile, P_createdBy,NOW()
            );
        
            SET i = i + 1;
        END WHILE;
  
  
  
  SET P_MSG_OUT ='2';
   ELSE 
       SET P_MSG_OUT ='4';
   end if;
  ELSEIF(P_Action='C') THEN
  Update T_PROJECT_TENDER_TECHNICAL set updatedBy=P_createdBy,updatedOn=NOW(),deletedFlag=1 where ID=P_ID;
  UPDATE T_TENDER_TECHNICAL_BIDDER_DTLS SET deletedFlag = 1,updatedBy=P_createdBy, updatedOn=NOW()
        WHERE FIND_IN_SET(technicalId, P_ID) > 0 AND Id > 0;
 SET P_MSG_OUT='3';
 END IF;
 COMMIT;
END ;;
