  delete from  M_POR_DESIGNATIONLINKACCESS  WHERE  INTDESINATIONID= P_INTDESINATIONID and INTPROJECTID=P_INTPROJECTID;
  
  
           OPEN cursor_List;
             loop_List: LOOP
             FETCH cursor_List INTO cur_INTUSERID;
              IF cursor_List_isdone THEN
              LEAVE loop_List;
              END IF;
               delete from M_POR_LINKACCESS    WHERE  INTUSERID= cur_INTUSERID and INTPROJECTID=P_INTPROJECTID;
			END LOOP loop_List;
            CLOSE cursor_List;


			 SELECT * from M_JK_SUCCESSMESSAGE where successid=3; 
  
   ELSE IF (P_Action='DPU')Then  #####Delete Permission Userwise

