  Delete from M_POR_LINKACCESS   WHERE  INTUSERID= P_INTUSERID and INTPROJECTID=P_INTPROJECTID;
  
    
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=3;             	
			

 ELSE IF (P_Action='AEU')Then  #####Set permission for User  Data(Add/Update)


