      VALUES
        (mvarintMaxID,P_INTUSERID,P_intpLinkId,P_TINACCESS,P_intuser,curdate(),P_intuser,curdate(),1,P_INTPROJECTID);
			 
			 
			 SELECT * from M_JK_SUCCESSMESSAGE where successid=9;             	
			

 ELSE IF (P_Action='RUP')Then  #####Remove user permission  

     update M_POR_LINKACCESS
    set bitstatus=0, DTMUPDATEDON=curdate(),intupdatedby=P_intuser where intuserid=P_INTUSERID;
    
