CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_TakeAction_Project_Tender`(
  OUT P_MSG_OUT VARCHAR(100),
  IN P_Action CHAR(30),
  IN P_createdBy INT,
  IN P_ForwardTo INT,
  IN P_actiontakenRemarks VARCHAR(300),
  IN P_actiontakenFile VARCHAR(250),
  IN P_statusId INT,
  IN P_Id INT
)
BEGIN
    DECLARE v_error_message TEXT;
    DECLARE v_error_code INT;
    DECLARE v_error_procedure VARCHAR(200) DEFAULT 'USP_TakeAction_Project_Tender';
    DECLARE v_assignTo varchar(50);
    DECLARE v_username varchar(15);
    DECLARE P_Idd INT;
    DECLARE V_gstNo VARCHAR(50);
    DECLARE V_contactNo VARCHAR(50);
    DECLARE v_contactPerson VARCHAR(50);
    DECLARE V_faxNo VARCHAR(50);
    DECLARE V_emailId VARCHAR(100);
    DECLARE V_mobileNo VARCHAR(15);
    DECLARE V_address VARCHAR(250);
    -- Error Handling

    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        GET DIAGNOSTICS CONDITION 1
           v_error_message = MESSAGE_TEXT,
           v_error_code = MYSQL_ERRNO;
        
        CALL usp_write_error_log(v_error_message, v_error_code, v_error_procedure, P_Action, NULL);
        ROLLBACK; -- Move rollback after logging the error
        SET P_MSG_OUT = '0';
    END;

    START TRANSACTION;

