CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_TicketRaise`(
    OUT P_MSG_OUT VARCHAR(100), 
    IN P_Action CHAR(30),
    IN P_ticketId INT,
    IN P_issueTypeId INT,
    IN P_moduleId INT,
    IN P_subModuleId INT,
    IN P_issueDocPath VARCHAR(255),
    IN P_description VARCHAR(250),
    IN P_UserId INT,
    IN P_actionTakenUserid INT,
    IN P_statusId INT
)
BEGIN
DECLARE record_count INT DEFAULT 0;
DECLARE TktId INT DEFAULT 0;
DECLARE TktCode VARCHAR(100);
DECLARE desgName VARCHAR(100);
DECLARE currentYear INT;
DECLARE startYear INT;
DECLARE endYear INT;
DECLARE subModuleCount INT;
DECLARE sqlQuery TEXT;

IF P_Action = 'I' THEN
    SELECT COUNT(*) INTO record_count FROM T_VENDOR_RAISETICKET_ADD WHERE ticketId = P_ticketId;
    
    IF record_count = 0 THEN
        SELECT ticketId INTO TktId FROM T_VENDOR_RAISETICKET_ADD ORDER BY ticketId DESC LIMIT 1;
        SET TktId = IFNULL(TktId, 0) + 1;
        
        SET @currentYear = YEAR(NOW());
		SET @startYear = IF(DATE(NOW()) > STR_TO_DATE(CONCAT(@currentYear, '-03-30'), '%Y-%m-%d'), @currentYear + 1, @currentYear);
		SET @endYear = @startYear + 1;

		SET TktCode = CONCAT(
			DATE_FORMAT(STR_TO_DATE(CONCAT(@startYear, '-01-01'), '%Y-%m-%d'), '%y'),
			DATE_FORMAT(STR_TO_DATE(CONCAT(@endYear, '-01-01'), '%Y-%m-%d'), '%y'),
			LPAD(P_moduleId, 2, '0'),
			LPAD(P_subModuleId, 2, '0'),
			LPAD(TktId, 4, '0')
		);

     SET subModuleCount = (SELECT COUNT(*) FROM M_SubModule_Master WHERE ModuleId = P_moduleId AND deletedFlag = 0);

INSERT INTO T_VENDOR_RAISETICKET_ADD 
    (issueTypeId, ticketCode, moduleId, subModuleId, issueDocPath, description, createdBy, createdOn) 
VALUES
    (P_issueTypeId, 
     TktCode, 
     P_moduleId,
     CASE 
        WHEN subModuleCount > 0 THEN P_subModuleId 
        ELSE NULL 
     END, 
     P_issueDocPath, 
     P_description, 
     P_UserId, 
     NOW());


        SET P_MSG_OUT = '1'; 
    ELSE
        SET P_MSG_OUT = '4'; 
    END IF;
END IF;

-- Update Operation
IF P_Action = 'U' THEN
    SELECT COUNT(*) INTO record_count FROM T_VENDOR_RAISETICKET_ADD WHERE ticketId = P_ticketId;

    IF record_count > 0 THEN
        UPDATE T_VENDOR_RAISETICKET_ADD 
        SET 
            issueTypeId = P_issueTypeId, 
            moduleId = P_moduleId, 
            subModuleId = P_subModuleId, 
            issueDocPath = P_issueDocPath, 
            description = P_description, 
            updatedBy = P_UserId, 
            updatedOn = NOW()
        WHERE 
            ticketId = P_ticketId 
            AND deletedFlag = 0;

        SET P_MSG_OUT = '2';  
    ELSE
        SET P_MSG_OUT = '4';  
    END IF;
END IF;

-- Delete Operation
IF P_Action = 'D' THEN
    UPDATE T_VENDOR_RAISETICKET_ADD 
    SET updatedOn = NOW(), updatedBy = P_UserId, deletedFlag = 1 
    WHERE ticketId = P_ticketId;
    SET P_MSG_OUT = '1';
END IF;

-- Lookup lists
IF P_Action = 'BBT' THEN
    SELECT issueTypeId, IssueType FROM M_IssueType_Master WHERE deletedFlag = 0 ORDER BY IssueType;
END IF; 

IF P_Action = 'BM' THEN
    SELECT moduleId, moduleName FROM M_Module_Master WHERE deletedFlag = 0 ORDER BY moduleName;
END IF; 

IF P_Action = 'BSM' THEN
    SELECT subModuleId, subModuleName FROM M_SubModule_Master WHERE moduleId = P_moduleId AND deletedFlag = 0 ORDER BY subModuleName;
END IF; 

-- View single ticket
IF P_Action = 'VS' THEN
    SELECT VRD.ticketId,VRD.ticketCode, 
    VRD.issueTypeId,
    ( SELECT  IssueType FROM M_IssueType_Master WHERE issueTypeId=VRD.issueTypeId and deletedFlag = 0) AS IssueType, 
    VRD.moduleId, VRD.subModuleId,
    VRD.description,
        (SELECT moduleName FROM M_Module_Master MM WHERE MM.moduleId = VRD.moduleId) AS moduleName,
        (SELECT subModuleName FROM M_SubModule_Master SM WHERE SM.subModuleId = VRD.subModuleId) AS SubmoduleName,
        VRD.issueDocPath,
        DATE_FORMAT(VRD.createdOn, '%d-%b-%Y') AS createdOn,
        CASE 
            WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) IS NULL THEN 'Pending'
            WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 1 THEN 'Closed'
             WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 2 THEN 'Close and Not Releated'
              WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 3 THEN 'InProgress'
               WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 4 THEN 'Forwarded to Technical Team'
            ELSE 'Other Status'
        END AS TicketStatus,
        IFNULL((SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1), 0) AS statusId
    FROM T_VENDOR_RAISETICKET_ADD VRD WHERE VRD.ticketId = P_ticketId
    ORDER BY VRD.createdOn DESC;
END IF;


IF P_Action = 'VA' THEN
 
 SELECT NVCHDESIGNAME INTO desgName from M_ADM_DESIGNATION where INTDESIGID= (SELECT INTDESIGNATIONID FROM M_POR_USER WHERE INTUSERID = P_UserId);
   
  SET @sqlQuery = '
    SELECT 
        VRD.ticketId,
        VRD.ticketCode,
        (SELECT IssueType FROM M_IssueType_Master WHERE issueTypeId = VRD.issueTypeId AND deletedFlag = 0) AS IssueType,
        VRD.issueTypeId,
        VRD.moduleId,
        VRD.subModuleId,
        VRD.description,
        (SELECT moduleName FROM M_Module_Master MM WHERE MM.moduleId = VRD.moduleId) AS moduleName,
        CASE 
            WHEN VRD.subModuleId IS NULL OR VRD.subModuleId = '''' THEN 
                (SELECT moduleName FROM M_Module_Master MM WHERE MM.moduleId = VRD.moduleId)
            ELSE 
                (SELECT subModuleName FROM M_SubModule_Master SM WHERE SM.subModuleId = VRD.subModuleId)
        END AS subModuleName,
        VRD.issueDocPath,
        DATE_FORMAT(VRD.createdOn, ''%d-%b-%Y'') AS createdOn,
        CASE 
            WHEN TRR.statusId IS NULL THEN ''Pending''
            WHEN TRR.statusId = 1 THEN ''Closed''
            WHEN TRR.statusId = 2 THEN ''Closed and not Related''
            WHEN TRR.statusId = 3 THEN ''Inprogress''
            WHEN TRR.statusId = 4 THEN ''Forward to Technical team''
            WHEN TRR.statusId = 5 THEN ''Re-Open''
            ELSE ''Other Status''
        END AS TicketStatus,
        IFNULL(TRR.statusId, 0) AS statusId,
        VRD.createdBy
    FROM T_VENDOR_RAISETICKET_ADD VRD 
    LEFT JOIN (
        SELECT ticketId, statusId
        FROM T_VENDOR_RAISETICKET_RESPONSE 
        WHERE (ticketId, createdOn) IN (
            SELECT ticketId, MAX(createdOn) 
            FROM T_VENDOR_RAISETICKET_RESPONSE 
            WHERE deletedFlag = 0 
            GROUP BY ticketId
        )
    ) TRR ON VRD.ticketId = TRR.ticketId 
    WHERE VRD.deletedFlag = 0';


    IF desgName NOT IN ('PMU (Support Team)','Technical Team') THEN
        SET @sqlQuery = CONCAT(@sqlQuery, ' AND VRD.createdBy = ', P_UserId);
    END IF;

    IF desgName = 'Technical Team' THEN
        SET @sqlQuery = CONCAT(@sqlQuery, ' AND TRR.statusId IN (4) ');
    END IF;

    SET @sqlQuery = CONCAT(@sqlQuery, ' ORDER BY VRD.createdOn DESC;');

    PREPARE stmt FROM @sqlQuery;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;


END IF;

-- Lookup user for a ticket
IF P_Action = 'LU' THEN
    SELECT TRD.createdBy AS UserId,
        (SELECT UM.VCHFULLNAME FROM M_POR_USER UM WHERE UM.INTUSERID = TRD.createdBy) AS username,
        TRD.TicketCode,
        TRD.issueDocPath,
        TRD.description
    FROM T_VENDOR_RAISETICKET_ADD TRD  WHERE TRD.ticketId = P_ticketId;
END IF;

-- Insert response
IF P_Action = 'IR' THEN
    INSERT INTO T_VENDOR_RAISETICKET_RESPONSE 
        (ticketId, actionTakenBy, actionTakenUserid, actionDate, actionRemarks, actionDocPath, statusId, createdBy, createdOn)
    VALUES
        (P_ticketId, P_UserId, P_actionTakenUserid, NOW(), P_description, P_issueDocPath, P_statusId, P_UserId, NOW());

    SET P_MSG_OUT = '1';  
END IF;

IF P_Action = 'RO' THEN
    INSERT INTO T_VENDOR_RAISETICKET_RESPONSE 
        (ticketId, actionTakenUserid, actionDate, actionRemarks, actionDocPath, statusId, createdBy, createdOn)
    VALUES
        (P_ticketId, P_UserId, NOW(), P_description, P_issueDocPath, 5, P_UserId, NOW());

    SET P_MSG_OUT = '1';  
END IF;

IF P_Action = 'VR' THEN
     SELECT
     actionRemarks as description,
     actionDocPath as issueDocPath,
	 DATE_FORMAT(createdOn, '%d-%b-%Y') AS createdOn,
    CASE 
        WHEN statusId IS NULL THEN 'Pending'
        WHEN statusId = 1 THEN 'Closed'
        WHEN statusId = 2 THEN 'Closed and not Related'
        WHEN statusId = 3 THEN 'Inprogress'
        WHEN statusId = 4 THEN 'Forward to Technical team'
		WHEN statusId = 5 THEN 'Re-Open'
        ELSE 'Other Status'
    END AS TicketStatus
    ,(select VCHFULLNAME from M_POR_USER where INTUSERID=createdBy)as username 
    FROM T_VENDOR_RAISETICKET_RESPONSE where ticketId=P_ticketId;
END IF;

END ;;
