        WHERE  ticketId=P_ticketId;
        SET P_MSG_OUT = '1';
    END IF;
    
    IF P_Action = 'BBT' THEN
        select issueTypeId,IssueType from M_IssueType_Master where deletedFlag=0 order by IssueType;
    END IF; 
    
     IF P_Action = 'BM' THEN
       select moduleId,moduleName from M_Module_Master where deletedFlag=0 order by moduleName;
    END IF; 
    
     IF P_Action = 'BSM' THEN
        select subModuleId,subModuleName from M_SubModule_Master where ModuleId=P_moduleId and deletedFlag=0 order by subModuleName;
    END IF; 
    
	IF P_Action = 'VS' THEN
	select VRD.ticketId,VRD.issueName,VRD.issueTypeId,VRD.moduleId, VRD.subModuleId,VRD.description,
	(select moduleName from M_Module_Master MM where MM.moduleId=VRD.moduleId) as moduleName,
	(select subModuleName from M_SubModule_Master SM where SM.subModuleId=VRD.subModuleId) as SubmoduleName,
	VRD.issueDocPath,
	DATE_FORMAT(VRD.createdOn, '%d-%b-%Y') AS createdOn,
	CASE 
        WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId) IS NULL THEN 'Ticket Response Pending'
        WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId) = 1 THEN 'Ticket Closed'
        ELSE 'Other Status'
    END AS TicketStatus,
   IFNULL((SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1), 0) AS statusId

	 from T_VENDOR_RAISETICKET_ADD VRD where  VRD.ticketId = P_ticketId
	ORDER BY VRD.createdOn DESC;     
    
    END IF; 
 
 IF P_Action = 'VA' THEN

    SET desgId = (SELECT INTDESIGNATIONID FROM M_POR_USER WHERE INTUSERID = P_UserId);

    IF desgId = 7 THEN
        SELECT VRD.ticketId, VRD.issueName, VRD.issueTypeId, VRD.moduleId, VRD.subModuleId, VRD.description,
            (SELECT moduleName FROM M_Module_Master MM WHERE MM.moduleId = VRD.moduleId) AS moduleName,
            (SELECT subModuleName FROM M_SubModule_Master SM WHERE SM.subModuleId = VRD.subModuleId) AS SubmoduleName,
            VRD.issueDocPath,
            DATE_FORMAT(VRD.createdOn, '%d-%b-%Y') AS createdOn,
            CASE 
                WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) IS NULL THEN 'Ticket Response Pending'
                WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 1 THEN 'Ticket Closed'
                ELSE 'Other Status'
            END AS TicketStatus,
            IFNULL((SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1), 0) AS statusId
        FROM T_VENDOR_RAISETICKET_ADD VRD where VRD.createdBy=P_UserId and VRD.deletedFlag=0
        ORDER BY VRD.createdOn DESC;

    ELSEIF desgId = 1 THEN
        SELECT VRD.ticketId, VRD.issueName, VRD.issueTypeId, VRD.moduleId, VRD.subModuleId, VRD.description,
            (SELECT moduleName FROM M_Module_Master MM WHERE MM.moduleId = VRD.moduleId) AS moduleName,
            (SELECT subModuleName FROM M_SubModule_Master SM WHERE SM.subModuleId = VRD.subModuleId) AS SubmoduleName,
            VRD.issueDocPath,
            DATE_FORMAT(VRD.createdOn, '%d-%b-%Y') AS createdOn,
            CASE 
                WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) IS NULL THEN 'Ticket Response Pending'
                WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 1 THEN 'Ticket Closed'
                ELSE 'Other Status'
            END AS TicketStatus,
            IFNULL((SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1), 0) AS statusId
        FROM T_VENDOR_RAISETICKET_ADD VRD where VRD.deletedFlag=0
        ORDER BY VRD.createdOn DESC;

    ELSE
        SELECT VRD.ticketId, VRD.issueName, VRD.issueTypeId, VRD.moduleId, VRD.subModuleId, VRD.description,
            (SELECT moduleName FROM M_Module_Master MM WHERE MM.moduleId = VRD.moduleId) AS moduleName,
            (SELECT subModuleName FROM M_SubModule_Master SM WHERE SM.subModuleId = VRD.subModuleId) AS SubmoduleName,
            VRD.issueDocPath,
            DATE_FORMAT(VRD.createdOn, '%d-%b-%Y') AS createdOn,
            CASE 
                WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) IS NULL THEN 'Ticket Response Pending'
                WHEN (SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1) = 1 THEN 'Ticket Closed'
                ELSE 'Other Status'
            END AS TicketStatus,
            IFNULL((SELECT statusId FROM T_VENDOR_RAISETICKET_RESPONSE VRR WHERE VRR.ticketId = VRD.ticketId LIMIT 1), 0) AS statusId
        FROM T_VENDOR_RAISETICKET_ADD VRD where VRD.createdBy=P_UserId and VRD.deletedFlag=0
        ORDER BY VRD.createdOn DESC;
    END IF;
END IF;

 
    IF P_Action = 'LU' THEN
    select TRD.createdBy as UserId,
	(select UM.VCHFULLNAME from M_POR_USER UM where UM.INTUSERID=TRD.createdBy) as username
	 from T_VENDOR_RAISETICKET_ADD TRD  where TRD.ticketId=P_ticketId ;
    END IF;
    
IF P_Action = 'IR' THEN
    INSERT INTO T_VENDOR_RAISETICKET_RESPONSE 
        (ticketId, actionTakenBy, actionTakenUserid, actionDate, actionRemarks, actionDocPath, statusId, createdBy, createdOn)
    VALUES
        (P_ticketId, P_UserId, P_actionTakenUserid, NOW(), P_description, P_issueDocPath, 1, P_UserId, NOW());

    SET P_MSG_OUT = '1';  -- Success
END IF;


    
END ;;
