CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_T_Project_AssignWork`(
   OUT P_MSG_OUT VARCHAR(100),
   IN P_Action CHAR(30),
   IN p_tenderId INT,
   IN p_projectId INT,
   IN P_AssignTo varchar(50),
   IN P_AgreementAmount INT,
   IN p_bidderId INT,
   IN p_panNo VARCHAR(20),
   IN p_gstNo VARCHAR(20),
   IN p_licenseNo VARCHAR(45),
   IN p_contactNo VARCHAR(15),
   IN p_faxNo VARCHAR(20),
   IN p_emailId VARCHAR(100),
   IN p_mobileNo VARCHAR(15),
   IN p_contactPerson VARCHAR(100),
   IN P_contactPersonMobile VARCHAR(12),
   IN p_address VARCHAR(100),
   IN p_bgStatus INT,
   IN p_projectDuration INT,
   IN p_startDate DATE,
   IN p_endDate DATE,
   IN p_letterDocPath VARCHAR(1000),
   IN P_createdBy INT,
   IN P_Id INT,
   IN P_insuranceExpiry DATE,
   IN P_labourLicenseExpiry DATE,
   IN P_BankList TEXT, -- JSON input 
   IN P_BankComponentList TEXT,
   IN P_insuranceFile VARCHAR(250),
   IN P_labourLicenseFile VARCHAR(250),
   In P_executionMode int
)
BEGIN
    DECLARE v_error_message TEXT;
    DECLARE v_error_code INT;
    DECLARE v_error_procedure VARCHAR(200) DEFAULT 'USP_T_Project_AssignWork';
    DECLARE i INT DEFAULT 0;
    DECLARE total INT DEFAULT 0;
    DECLARE jsonData JSON;
    DECLARE v_InstrumentType VARCHAR(255);
    DECLARE v_PGAmount DECIMAL(10,2);
    DECLARE v_BankName VARCHAR(100);
    DECLARE v_ValidTill DATE;
    DECLARE v_UploadPGDocumentPath VARCHAR(1000);
    DECLARE v_uploadLetterdoc VARCHAR(250);
    
     DECLARE ii INT DEFAULT 0;
    DECLARE totall INT DEFAULT 0;
    DECLARE jsonDataa JSON;
    DECLARE v_payCompId INT;
     DECLARE v_Amount decimal(18,2);
    -- Error Handling
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        GET DIAGNOSTICS CONDITION 1
           v_error_message = MESSAGE_TEXT,
           v_error_code = MYSQL_ERRNO;
        ROLLBACK;
        CALL usp_write_error_log(v_error_message, v_error_code, v_error_procedure, P_Action, NULL);
        SET P_MSG_OUT = '0';
    END;

    START TRANSACTION;
      set @@sql_mode = '';  
      set P_BankList = replace(P_BankList,'\"','"'); 
	 set P_BankComponentList = replace(P_BankComponentList,'\"','"'); 
    -- Validate JSON Input
    IF JSON_VALID(P_BankList) = 1 THEN
        SET jsonData = CAST(P_BankList AS JSON);
        SET total = JSON_LENGTH(jsonData);
    ELSE
        SET jsonData = '[]';
        SET total = 0;
    END IF;
    
-- Bank component List
    IF JSON_VALID(P_BankComponentList) = 1 THEN
        SET jsonDataa = CAST(P_BankComponentList AS JSON);
        SET totall = JSON_LENGTH(jsonDataa);
    ELSE
        SET jsonDataa = '[]';
        SET totall = 0;
    END IF;

    IF (P_Action = 'A') THEN
     /*IF ((select count(1) from T_Project_AssignWork where projectId=p_projectId and deletedFlag=0)=0)
     THEN*/
        INSERT INTO T_Project_AssignWork (
            tenderId, projectId,AssignTo,AgreementAmount,insuranceFile,insuranceExpiry,labourLicenseFile,labourLicenseExpiry,bidderId, panNo, gstNo, licenseNo, contactNo, faxNo, 
            emailId, mobileNo, contactPerson, contactPersonMobile, address, bgStatus, 
            projectDuration,startDate, endDate, letterDocPath, createdBy, deletedFlag,statusId,actionTakenUserid,actionTakenBy,actionTakenOn,executionMode
        ) VALUES (
            p_tenderId, p_projectId,P_AssignTo,P_AgreementAmount,P_insuranceFile,P_insuranceExpiry,P_labourLicenseFile,P_labourLicenseExpiry, p_bidderId, p_panNo, p_gstNo, p_licenseNo, p_contactNo, p_faxNo, 
            p_emailId, p_mobileNo, p_contactPerson, P_contactPersonMobile, p_address,p_bgStatus, 
            p_projectDuration, p_startDate, p_endDate, p_letterDocPath,P_createdBy, 0, 0,P_createdBy,P_createdBy,NOW(),P_executionMode
        );
   
   IF(P_executionMode!=1)
   THEN
        -- Get Last Inserted ID
        SET P_Id = LAST_INSERT_ID();

        WHILE i < total DO
            SET v_InstrumentType = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].InstrumentType'))), '');
            SET v_PGAmount = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].PGAmount'))), 0);
            SET v_BankName = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].BankName'))), '');
            SET v_ValidTill = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].ValidTill'))), NULL);
            SET v_UploadPGDocumentPath = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].UploadPGDocumentPath'))), NULL);
            -- SET v_uploadLetterdoc = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].uploadLetterdoc'))), NULL);

            INSERT INTO T_PROJECT_BIDDER_PBGDTLS (
                assignWorkid, tenderId, projectId, instrumentType, pgAmount, bankName, 
                validaityDate, pgDocPath, createdBy
            ) VALUES (
                P_Id, p_tenderId, p_projectId, v_InstrumentType, v_PGAmount, v_BankName, 
                v_ValidTill, v_UploadPGDocumentPath, P_createdBy
            );
        
            SET i = i + 1;
        END WHILE;
    
     WHILE ii < totall DO
            SET v_payCompId = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonDataa, CONCAT('$[', ii, '].payCompId'))), 0);
            SET v_Amount = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonDataa, CONCAT('$[', ii, '].componentAmount'))), 0);          
            
        INSERT INTO T_PROJECT_PAYMENTCOMP_DTLS(payCompId,tenderId,projectId,Amount,createdBy)
        values(v_payCompId,p_tenderId,p_projectId,v_Amount,P_createdBy);
        SET ii = ii + 1;
        END WHILE;
end if;
        SET P_MSG_OUT = 1; -- Success
  /*ELSE 
          SET P_MSG_OUT =4;
         end if;*/
  
    ELSEIF (P_Action = 'B') THEN
    /* IF ((select count(1) from T_Project_AssignWork where projectId=p_projectId and deletedFlag=0)=0)
     THEN*/
       UPDATE T_Project_AssignWork 
  SET 
    projectId = IFNULL(p_projectId, projectId),
    AssignTo = IFNULL(P_AssignTo, AssignTo),
    AgreementAmount = IFNULL(P_AgreementAmount, AgreementAmount),
     insuranceFile=IFNULL(P_insuranceFile,insuranceFile),
    insuranceExpiry = IFNULL(P_insuranceExpiry, insuranceExpiry),
    labourLicenseFile = IFNULL(P_labourLicenseFile,labourLicenseFile),
    labourLicenseExpiry = IFNULL(P_labourLicenseExpiry, labourLicenseExpiry),
    bidderId = IFNULL(p_bidderId, bidderId),
    panNo = IFNULL(p_panNo, panNo),
    gstNo = IFNULL(p_gstNo, gstNo),
    licenseNo = IFNULL(p_licenseNo, licenseNo),
    contactNo = IFNULL(p_contactNo, contactNo),
    faxNo = IFNULL(p_faxNo, faxNo),
    emailId = IFNULL(p_emailId, emailId),
    mobileNo = IFNULL(p_mobileNo, mobileNo),
    contactPerson = IFNULL(p_contactPerson, contactPerson),
    contactPersonMobile = IFNULL(P_contactPersonMobile, contactPersonMobile),
    address = IFNULL(p_address, address),
    bgStatus = IFNULL(p_bgStatus, bgStatus),
    projectDuration = IFNULL(p_projectDuration, projectDuration),
    startDate = IFNULL(p_startDate, startDate),
    endDate = IFNULL(p_endDate, endDate),
    letterDocPath = IFNULL(p_letterDocPath, letterDocPath), 
    updatedBy = IFNULL(P_createdBy, updatedBy),
    updatedOn = NOW(),
    executionMode = IFNULL(P_executionMode,executionMode)
  WHERE id = P_Id;
   IF(P_executionMode!=1)
   THEN
		UPDATE T_PROJECT_BIDDER_PBGDTLS SET deletedFlag = 1,updatedBy=createdBy, updatedOn=NOW()
        WHERE FIND_IN_SET(assignWorkid, P_Id) > 0 AND Assignid > 0;
        
       WHILE i < total DO
            SET v_InstrumentType = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].InstrumentType'))), '');
            SET v_PGAmount = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].PGAmount'))), 0);
            SET v_BankName = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].BankName'))), '');
            SET v_ValidTill = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].ValidTill'))), NULL);
            SET v_UploadPGDocumentPath = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonData, CONCAT('$[', i, '].UploadPGDocumentPath'))), NULL);      

            INSERT INTO T_PROJECT_BIDDER_PBGDTLS (
                assignWorkid, tenderId, projectId, instrumentType, pgAmount, bankName, 
                validaityDate, pgDocPath, createdBy
            ) VALUES (
                P_Id, p_tenderId, p_projectId, v_InstrumentType, v_PGAmount, v_BankName, 
                v_ValidTill, v_UploadPGDocumentPath, P_createdBy
            );
        
            SET i = i + 1;
        END WHILE;
    
     update T_PROJECT_PAYMENTCOMP_DTLS SET deletedFlag = 1,updatedBy=P_createdBy, updatedOn=NOW() 
     WHERE FIND_IN_SET(projectId, p_projectId) > 0 AND headId > 0;
        
        WHILE ii < totall DO
            SET v_payCompId = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonDataa, CONCAT('$[', ii, '].payCompId'))), 0);
            SET v_Amount = IFNULL(JSON_UNQUOTE(JSON_EXTRACT(jsonDataa, CONCAT('$[', ii, '].componentAmount'))), 0);          
            
        INSERT INTO T_PROJECT_PAYMENTCOMP_DTLS(payCompId,tenderId,projectId,Amount,createdBy)
        values(v_payCompId,p_tenderId,p_projectId,v_Amount,P_createdBy);
        SET ii = ii + 1;
        END WHILE;
    
	IF( (SELECT COUNT(*) FROM T_Project_AssignWork_Log  WHERE tenderId = p_tenderId AND status = 0 AND deletedFlag = 0)=0)
THEN
    -- Insert Insurance Entry
    INSERT INTO T_Project_AssignWork_Log (assignId, expirytype, expiryDate, expiryFileName, status, tenderId, createdby, createdOn)
    VALUES (P_Id, 'Insurance', P_insuranceExpiry, P_insuranceFile, 0, p_tenderId, P_createdBy, NOW());

    -- Insert Labour Entry
    INSERT INTO T_Project_AssignWork_Log (assignId, expirytype, expiryDate, expiryFileName, status, tenderId, createdby, createdOn)
    VALUES (P_Id, 'Labour', P_labourLicenseExpiry, P_labourLicenseFile, 0, p_tenderId, P_createdBy, NOW());
ELSE
    -- Update Insurance Entry
    UPDATE T_Project_AssignWork_Log 
    SET expiryDate = IFNULL(P_insuranceExpiry,expiryDate),
        expiryFileName = IFNULL(P_insuranceFile,expiryFileName),
        tenderId = IFNULL(p_tenderId,tenderId),
        updatedBy = P_createdBy,
        updatedOn = NOW(),
        status = 0
    WHERE assignId = P_Id 
      AND expirytype = 'Insurance' 
      AND LogId > 0;  

    -- Update Labour Entry
    UPDATE T_Project_AssignWork_Log 
    SET expiryDate = IFNULL(P_labourLicenseExpiry,expiryDate),
        expiryFileName = IFNULL(P_labourLicenseFile,expiryFileName),
        tenderId = IFNULL(p_tenderId,tenderId),
        updatedBy = P_createdBy,
        updatedOn = NOW(),
        status = 0
    WHERE assignId = P_Id 
      AND expirytype = 'Labour' 
      AND LogId > 0;
END IF;

      END IF;
        SET P_MSG_OUT = 2;
       /*  ELSE 
          SET P_MSG_OUT ='4';
         end if;*/
      
    -- Delete Assign Work
    ELSEIF (P_Action = 'C') THEN
        UPDATE T_Project_AssignWork SET updatedBy= P_createdBy,updatedOn=NOW(),deletedFlag=1 WHERE id = P_Id;
        SET P_MSG_OUT = '3';
  
    END IF;
  
    COMMIT;
END ;;
