CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_T_QUERYMANAGEMENT`(
OUT P_MSG_OUT VARCHAR(100), 
    IN P_Action CHAR(30),
	IN P_QueryTypeId INT,
    IN p_QueryId INT,
    IN P_queryDocPath varchar(100),
    IN p_photoDocPath VARCHAR(100),
    IN P_description VARCHAR(100),
    IN P_UserId INT,
    IN P_projectId INT,
	IN P_actionTakenUserid INT,          
    IN P_issueDocPath VARCHAR(100),      
    IN P_statusId INT                  
    
)
BEGIN
 DECLARE V_QueryId INT;
  DECLARE v_error_message TEXT;
    DECLARE v_error_code INT;
    DECLARE v_error_procedure VARCHAR(200) DEFAULT 'USP_T_QUERYMANAGEMENT';
  --  DECLARE v_username varchar(15);
    -- Error Handling

    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        GET DIAGNOSTICS CONDITION 1
           v_error_message = MESSAGE_TEXT,
           v_error_code = MYSQL_ERRNO;
        
        CALL usp_write_error_log(v_error_message, v_error_code, v_error_procedure, P_Action, NULL);
        ROLLBACK; -- Move rollback after logging the error
        SET P_MSG_OUT = '0';
    END;

    START TRANSACTION;
 

 IF P_Action = 'A' THEN -- insert data
       INSERT INTO T_VENDOR_QUERY_ADD 
		(queryTypeId,projectId, queryDocPath, photoDocPath, description, createdBy, createdOn)
		VALUES(P_QueryTypeId,
			P_projectId,
			P_queryDocPath,
			p_photoDocPath,
			P_description,
			P_UserId, 
            Now());
        SET P_MSG_OUT = '1';
 ELSEIF P_Action = 'B' THEN  -- For update data
    UPDATE T_VENDOR_QUERY_ADD
    SET 
        queryTypeId = P_QueryTypeId,
        projectId = P_projectId,
        queryDocPath = P_queryDocPath,
        photoDocPath = p_photoDocPath,
        description = P_description,
        updatedBy = P_UserId,
        updatedOn = NOW()
    WHERE queryId = P_QueryId;

    SET P_MSG_OUT = '2';
        
       ELSEIF P_Action = 'C' THEN  -- For action taken second time: update if exists, otherwise insert
    IF EXISTS (SELECT 1 FROM T_VENDOR_QUERY_RESPONSE WHERE QueryId = P_QueryId) THEN
        UPDATE T_VENDOR_QUERY_RESPONSE
        SET 
            actionTakenBy = P_UserId,
            actionTakenUserid = P_actionTakenUserid,
            actionDate = NOW(),
            actionRemarks = P_description,
            actionDocPath = P_issueDocPath,
            actionImagePath = 'Imagepath.png',
            statusId = P_statusId,
            updatedBy = P_UserId,
            updatedOn = NOW()
        WHERE QueryId = P_QueryId;
    ELSE
        INSERT INTO T_VENDOR_QUERY_RESPONSE 
            (QueryId, actionTakenBy, actionTakenUserid, actionDate, actionRemarks, actionDocPath, actionImagePath, statusId, createdBy, createdOn)
        VALUES
            (P_QueryId, P_UserId, P_actionTakenUserid, NOW(), P_description, P_issueDocPath, 'Imagepath.png', P_statusId, P_UserId, NOW());
 END IF;
    
    SET P_MSG_OUT = '1';
ELSEIF P_Action = 'D' THEN  -- For delete (soft delete)
    UPDATE T_VENDOR_QUERY_ADD
    SET 
        deletedFlag = 1,
        updatedBy = P_UserId,
        updatedOn = NOW()
    WHERE queryId = P_QueryId;
    SET P_MSG_OUT = '1';
END IF;
END ;;
