declare CountId int default 0;
 IF ( p_action = 'VF' ) THEN  #####-View AllUSER MASTER data
SET @row_number = 0; 
             SELECT /*+ parallel (4) */
                              (@row_number:=@row_number + 1) AS RowNo,
                             u.intuserid,
                             u.vchusername,
                             u.vchfullname,
                             u.intleveldetailid,
                             lev.nvchlevelname,
                             u.intdesignationid,
                             d.nvchdesigname,
                             u.intlocationid,
                             loc.nvchlocation,                             
