                             INNER JOIN M_ADM_LOCATION loc ON u.intlocationid=loc.intlocationid
                             INNER JOIN M_ADM_DESIGNATION d ON u.intdesignationid=d.intdesigid
                             LEFT JOIN M_ADM_LEVELDETAILS lev ON u.intleveldetailid = lev.intleveldetailid
                         WHERE
                                 u.bitstatus = 1
                             #AND lev.bitstatus = 1
                             AND d.bitstatus=1
                             AND  (p_intdesignationid=0 OR u.intdesignationid=p_intdesignationid)
                             AND (p_intlocationid=0 OR u.intlocationid=p_intlocationid)
