                             AND intuserid = p_intuserid;


ELSE IF(p_action='VU') THEN



      set CountId=( select /*+ parallel (4) */ count(*)  from M_POR_LINKACCESS where intuserid=p_intuserid and intprojectid=p_intprojectid AND bitStatus=1);

        if(CountId>0)
        then

         
        SELECT /*+ parallel (4) */ la.intaccessid,
