        CASE p_language WHEN "ENG" THEN gl.nvchglinkname WHEN "HIN" THEN gl.NVCHGLINKNAMEHIN ELSE gl.NVCHGLINKNAMEURD END AS nvchglinkname,
        CASE p_language WHEN "ENG" THEN pl.nvchplinkname WHEN "HIN" THEN pl.NVCHPLINKNAMEHIN ELSE pl.NVCHPLINKNAMEURD END AS nvchplinkname,
        pl.intfunctionid,
        f.vchfunction,
