        f.BITNEWTAB as tinnewtab,
        gl.vchiconclass
        FROM M_POR_LINKACCESS  la
        inner join M_ADM_PRIMARYLINK pl  on la.intplinkid=pl.intplinkid
        inner join M_ADM_FUNCTION f on f.intfunctionid=pl.intfunctionid
        inner join M_ADM_GLOBALLINK gl on pl.intglinkid=gl.intglinkid
        where la.bitstatus=1
        and pl.bitstatus=1
        and f.bitstatus=1
        and gl.bitstatus=1
        and la.intuserid=p_intuserid
