        where la.bitstatus=1
        and pl.bitstatus=1
        and f.bitstatus=1
        and gl.bitstatus=1
       and la.intdesinationid=p_intdesignationid
       and gl.intprojectid=p_intprojectid
        order by gl.intsortnum,pl.intslno;

