CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_ViewAprovalprocess`()
BEGIN
    /*SELECT 
        APC.Aid AS APCID,
        APC.intlevel AS LevelNO, 
        MAP.NVCHPLINKNAME AS NVCHPLINKNAME,
        MAP.INTPLINKID AS INTPLINKID,
        MPU.VCHUSERNAME AS VCHUSERNAME 
