        MAP.NVCHPLINKNAME AS NVCHPLINKNAME,
        MAP.INTPLINKID AS INTPLINKID,
         MPU.VCHUSERNAME ,
    MAD.NVCHDESIGNAME ,
        -- Bind whichever value is available (Username first, then Designation)
        COALESCE(MPU.VCHUSERNAME, MAD.NVCHDESIGNAME) AS UserOrDesignation,
        
        CASE 
            WHEN APC.intlevel = MAX(APC.intlevel) OVER (PARTITION BY MAP.NVCHPLINKNAME) THEN 1 
            ELSE 0 
        END AS IsHighestLevelNo
    FROM Approval_Config AS APC
    JOIN M_ADM_PRIMARYLINK AS MAP ON APC.FormName = MAP.INTPLINKID
    LEFT JOIN M_POR_USER AS MPU ON APC.intuserid = MPU.INTUSERID
    LEFT JOIN M_ADM_DESIGNATION AS MAD ON APC.intdesigid = MAD.intdesigid
    WHERE APC.intDeleteflag = 0
    ORDER BY MAP.NVCHPLINKNAME, APC.intlevel;    
