CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_AA_Approval`(
    IN P_Action CHAR(30),
    IN P_INT_ID INT
)
BEGIN
    IF P_Action = 'A' THEN
         SELECT 
            M.proposalid AS ProposalId, 
            (SELECT levelname FROM M_Adm_LevelDetails WHERE levelDetailId = districtid) AS District,
            M.subject AS ProposalSubject,
            (SELECT clientName FROM M_Client_Master WHERE clientid = submittedByid AND deletedflag = 0) AS ProposalSubmittedBy,
            (SELECT categoryName FROM M_Project_Category_Master WHERE categoryId = M.categoryid AND deletedflag = 0) AS ProposalCategory,
            (SELECT subCategoryName FROM M_Project_Subcategory_Master WHERE subcategoryId = M.subCategoryid AND deletedflag = 0) AS ProposalSubcategory,
            M.letterno AS ProposalLetterNo,
            DATE_FORMAT(M.proposalLetterDate, '%d-%b-%Y') AS ProposalLetterDate,
            M.letterdocpath AS ProposalLetterdocpath,
            TA.estimattedCost AS EstimatedCost,
            (select VCHUSERNAME from CPKG_1_USER where INTUSERID=TA.taApprovedBy) AS TAApprovedBy,
            TA.taLetterPath AS TAGeneratedLetterDocPath,
            DATE_FORMAT(taLetterDate, '%d-%b-%Y') AS TAGeneratedLetterDate,
            CASE 
                WHEN AA.aaLetterPath IS NOT NULL THEN 1 
                ELSE 0
            END AS IsAALetterGenerated,
            AA.aaLetterPath AS AAGeneratedLetterDocPath,
            DATE_FORMAT(AA.aaLetterGenDate, '%d-%b-%Y') AS AAGeneratedLetterDate,
            CASE 
                WHEN AA.otherDocPath IS NOT NULL THEN 1 
                ELSE 0
            END AS IsAAUploaded,
           # AA.signedAALetterPath AS AAUploadedFileDocPath,
            DATE_FORMAT(NOW(), '%d-%b-%Y') AS AAAUploadedDate,
            'Pending for AA' AS AAStatus,
            0 AS IsAADetailsApproved,
            '' AS CreatedByUserId
        FROM BUIDCO_devdb.M_Proposal_Master M
        LEFT JOIN T_PROPOSAL_TA_DETAILS TA ON M.proposalid = TA.proposalid and TA.deletedFlag=0
        LEFT JOIN T_PROPOSAL_AA_DETAILS AA ON M.proposalid = AA.proposalid and AA.deletedFlag=0
        WHERE (M.proposalStatus, M.STATUSID) IN ((2, 3), (3, 2)) AND M.deletedFlag = 0  
         # M.proposalStatus = 2 AND M.deletedFlag = 0  
          # AND M.STATUSID = 1;
        order by M.createdOn desc;
    ELSEIF P_Action = 'B' THEN
        select 
 M.proposalid AS ProposalId, 
            (SELECT levelname FROM M_Adm_LevelDetails WHERE levelDetailId = M.districtid) AS ProposalDistrict,
            M.subject AS ProposalSubject,
            (select VCHUSERNAME from M_POR_USER where INTUSERID=M.CreatedBy) AS ProposalCreatedBy,
            DATE_FORMAT(M.CreatedOn, '%d-%b-%Y') AS ProposalCreatedOn,
            TA.estimattedCost AS ProposalEstimatedAmount,
            TA.taLetterPath AS TASignedDocumentPath,
            AA.aaId AS AAId,
            AA.aaLetterPath AS AALetterPath,
            AA.aaLetterNo AS AALetterNo,
            DATE_FORMAT(AA.aaLetterDate, '%d-%b-%Y') AS AALetterDate,
            AA.schemeId AS AASchemeID,
            AA.aaApprovedBy AS AAApprovedBY,
           # AA.signedAALetterPath AS AASignedDocumentPath,
            AA.otherDocPath AS AAOtherDocuemntPath,
            AA.isMultipleProject AS IsMultipleProject,
           # '' AS ProjectDetailsList,
            AA.aaRemarks AS AARemarks,
            AA.createdBy AS AACreatedBy,
            (select IFNULL(SUM(area),0) from T_Proposal_Land_Information where proposalId=M.proposalid) as ProposalArea,
            CAST((
        SELECT 
            GROUP_CONCAT(
                CONCAT(
                    PTC.aaPartid, '|', 
                    PTC.projectPart, '|', 
					PTC.partDesc, '|', 
                    PTC.partAmount
                ) SEPARATOR '^'
            )
        FROM T_Proposal_AA_Part_Details PTC  -- Added alias here
        WHERE PTC.aaId = AA.aaId AND PTC.deletedFlag = 0
    ) AS CHAR) AS ProjectDetailsList
from M_Proposal_Master M
join T_PROPOSAL_TA_DETAILS TA ON M.proposalid = TA.proposalid and TA.deletedFlag=0
left join T_PROPOSAL_AA_DETAILS AA ON M.proposalid = AA.proposalid and AA.deletedFlag=0
where M.proposalid= P_INT_ID;
    END IF;
    
END ;;
