        LEFT JOIN T_Proposal_Action_Taken pmat ON M.proposalid=pmat.proposalId AND pmat.ActionId=(select MAX(ActionId) from T_Proposal_Action_Taken)
        WHERE M.deletedFlag = 0  
        AND (
					-- Enforce strict condition first
