										)
									)
							))
            )
        )
                    OR
                    (@searchtype='Approved') AND
                    (
						M.proposalStatus = 3
						AND M.statusId IN (2,1) 
						AND @createdBy = M.createdBy
					)
					-- If strict condition does not apply, execute other conditions
					OR 
					(
						M.proposalStatus = 3 
						AND M.statusId NOT IN (2,1) -- Ensures this runs only if (0,2) condition is false
						AND (
							-- If @createdBy is the officer, stop execution here
							@createdBy = M.officerId AND M.stepId = 2
							-- Otherwise, check Approval_Config condition
							OR (@createdBy <> M.officerId AND M.stepId > 2 AND @createdBy IN (
								SELECT usr.INTUSERID 
								FROM Approval_Config conf
								JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
								WHERE conf.intlevel = M.stepId
								AND conf.FormName = @AAWorkFlowID 
