								AND (
									conf.intlevel <> 3 
									OR (conf.intlevel = 3 
										AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = M.districtid)
										)
									)
							))
            )
        )
                    OR
                    (@searchtype='Cancelled') AND
                    (
						M.proposalStatus = 3
						AND M.statusId IN (2,1) 
						AND @createdBy = M.createdBy
					)
					-- If strict condition does not apply, execute other conditions
