CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_AssignProject`(
 IN P_Action CHAR(30),
 IN P_Id INT,
 IN P_PMT VARCHAR(10),
 IN P_createdBy INT
)
BEGIN
    IF (P_Action = 'A') THEN -- View Assign work View in list
	SELECT    
	DATE_FORMAT(PA.startDate, '%d-%b-%Y') AS startDate, DATE_FORMAT(PA.endDate, '%d-%b-%Y') AS endDate, PA.projectDuration, 
    PM.projectCode, PM.projectName, PM.proposalId,
    PA.AssignTo AS loaName, PA.AgreementAmount
    ,PP.letterNo ,PP.subject ,PP.districtId, PP.projectTypeid
    ,PA.Id AS id, PA.letterDocpath  
     , CASE 
        WHEN PA.statusId = 1 THEN CONCAT('Pending at ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(PA.actionTakenOn, '%d-%b-%Y'), 'N/A'))
        WHEN PA.statusId = 2 THEN CONCAT('Approved by ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(PA.actionTakenOn, '%d-%b-%Y'), 'N/A'))
        WHEN PA.statusId = 3 THEN CONCAT('Rejected by ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(PA.actionTakenOn, '%d-%b-%Y'), 'N/A'))
        WHEN PA.statusId = 4 THEN CONCAT('Revert by ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(PA.actionTakenOn, '%d-%b-%Y'), 'N/A'))
         WHEN PA.statusId = 0 THEN CONCAT('Pending by ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(PA.actionTakenOn, '%d-%b-%Y'), 'N/A'))
        ELSE 'Status Not Available'
    END AS statusMsg,
     UM.VCHUSERNAME as UserName,PA.createdBy, 
     CASE 
     WHEN PA.executionMode=1 THEN 'Department' ELSE 'Tender' End as vch_executionMode,
      PA.actionTakenBy,PA.statusId,PA.actionTakenOn,PA.actionTakenUserid
     FROM  T_Project_AssignWork PA 
     INNER JOIN T_PROJECT_MASTER PM ON PA.projectId = PM.projectId
     LEFT JOIN M_Proposal_Master PP ON PM.proposalId = PP.proposalId
      LEFT JOIN  M_POR_USER UM ON UM.INTUSERID = PA.actionTakenUserid
      LEFT JOIN M_ADM_DESIGNATION DM ON DM.INTDESIGID = UM.INTDESIGNATIONID
     WHERE  PA.deletedFlag = 0
	and PA.createdBy=P_createdBy
     or PA.actionTakenUserid =P_createdBy
     ORDER BY PA.id desc;
   
     ELSEIF (P_Action = 'B') THEN -- get project Details
     
    
IF P_PMT REGEXP '^[0-9]+$' THEN  
    SELECT DISTINCT PM.projectId, PM.projectCode,PTM.executionMode
FROM T_PROJECT_MASTER PM 
LEFT JOIN T_PROJECT_TENDER_FINANCIAL TF ON TF.projectId = PM.projectId
LEFT JOIN T_PROPOSAL_TS_DETAILS PTM ON PTM.proposalId = PM.proposalId
WHERE (PM.projectCode = P_PMT OR PM.projectCode LIKE CONCAT('%', P_PMT, '%'))  
AND PM.deletedFlag = 0  
AND (  
        (TF.deletedFlag = 0 AND TF.statusId = 2)  
        OR  
        (PTM.deletedFlag = 0 AND PTM.executionMode = 1)  
    );
ELSE  
    -- If P_PMT contains other formats, check if it is a tender number
    IF P_PMT LIKE 'TEN%' THEN  
        SELECT PM.projectId, PT.tenderNo
        FROM T_PROJECT_TENDER PT
        LEFT JOIN T_PROJECT_MASTER PM ON PT.projectId = PM.projectId
        INNER JOIN T_PROJECT_TENDER_FINANCIAL TF ON TF.projectId = PM.projectId
        WHERE PT.tenderNo LIKE CONCAT('%', P_PMT, '%') 
            AND TF.statusId = 2
            AND PM.deletedFlag = 0 
            AND TF.deletedFlag = 0;
    ELSE  
      
        SELECT DISTINCT PM.projectId,  
       COALESCE(NULLIF(PM.projectName, ''), PT.tenderNo) AS projectName,PTM.executionMode
FROM T_PROJECT_MASTER PM  
INNER JOIN M_Proposal_Master PPM ON PPM.proposalId = PM.proposalId  
LEFT JOIN T_PROJECT_TENDER PT ON PT.projectId = PM.projectId  
LEFT JOIN T_PROPOSAL_TS_DETAILS PTM ON PTM.proposalId = PPM.proposalId  
LEFT JOIN T_Project_AssignWork PA ON PA.projectId = PM.projectId  
WHERE LOWER(PM.projectName) LIKE LOWER(CONCAT('%', P_PMT, '%'))  
AND PM.deletedFlag = 0  
AND PPM.deletedFlag = 0  
AND (
       (PTM.deletedFlag = 0 AND PTM.executionMode = 1) 
       OR 
       (PA.deletedFlag = 0 AND PA.statusId = 2)
   );
    END IF;
END IF;
  
    
    
    
    ELSEIF (P_Action='C') THEN
     select    PA.id,PA.projectDuration,PA.AgreementAmount as agreementAmount, date_format(PA.startDate,'%d-%b-%Y')startDate, date_format(PA.endDate,'%d-%b-%Y')endDate,PA.letterDocpath as AssignDoc,
     date_format(PA.insuranceExpiry,'%d-%b-%Y')insuranceExpiry,PA.insuranceFile,PA.labourLicenseFile,date_format(PA.labourLicenseExpiry,'%d-%b-%Y')labourLicenseExpiry,PA.id, PA.gstNo, PA.panNo, PA.licenseNo, PA.contactNo,
     PA.faxNo, PA.address, PA.contactPerson,PA.contactPersonMobile, PA.emailId ,PA.AssignTo,PA.letterDocPath,PA.mobileNo,   
     PA.actionTakenBy,PA.statusId,PA.actionTakenOn,PA.actionTakenUserid
,CASE 
    WHEN PA.bgStatus = 1 THEN 'Yes' 
    WHEN PA.bgStatus = 0 THEN 'No' 
    ELSE 'Unknown' 
END AS bgStatus
    ,PCM.payCompId,PCM.PaymentComponent,PPD.Amount
     ,PM.projectId,PM.constructionArea, PM.length, PM.projectName
     ,PT.tenderId,PT.emdAmount,date_format(PT.openingDate,'%d-%b-%Y')openingDate,date_format(PT.submissionDate,'%d-%b-%Y')submissionDate,PT.nitDocFileName
     ,PPM.letterNo,PPM.proposerName
     ,CP1.VCHUSERNAME as SubmittedBy,PA.executionMode as vch_executionMode
     FROM  T_Project_AssignWork PA
	 LEFT JOIN T_PROJECT_PAYMENTCOMP_DTLS PPD ON PPD.projectId =PA.projectId 
	 LEFT JOIN M_PaymentComponent_Master PCM ON PCM.payCompId =PPD.payCompId
     LEFT JOIN T_PROJECT_TENDER PT ON PT.projectId = PA.projectId
     LEFT JOIN T_PROJECT_MASTER PM ON PM.projectId = PA.projectId
	 LEFT JOIN M_Proposal_Master PPM ON PPM.proposalId = PM.proposalId
     LEFT JOIN M_POR_USER CP1 ON CP1.INTUSERID = PPM.submittedByid
     where   PA.id=P_Id
    AND (PPD.deletedFlag = 0 OR PPD.deletedFlag IS NULL)
    AND (PCM.deletedFlag = 0 OR PCM.deletedFlag IS NULL)
    AND (PT.deletedFlag = 0 OR PT.deletedFlag IS NULL)
    AND (PM.deletedFlag = 0 OR PM.deletedFlag IS NULL)
    AND (PPM.deletedFlag = 0 OR PPM.deletedFlag IS NULL);

   -- Delete Record (Action: D)
   /*  ELSEIF (P_Action='D') THEN
     UPDATE T_Project_AssignWork SET  updatedBy = 1, updatedOn = NOW(), deletedFlag = 1 WHERE id = P_Id;
    
     SELECT 3;*/
     
     
     ELSEIF(P_Action='E') THEN -- for component type dropdown created by akash kumar
     select payCompId,PaymentComponent from M_PaymentComponent_Master where deletedFlag=0;

   /*  ELSEIF (P_Action='F') THEN 
    SELECT date_format(PA.startDate, '%d-%b-%Y') AS startDate, 
    date_format(PA.endDate, '%d-%b-%Y') AS endDate, PA.projectDuration, 
    PM.projectCode, PM.projectName, PM.proposalId,'AssignTo' AS loaName, 
    'AgreementAmount' AS loaAggrimentAmt, PP.letterNo
    ,PP.subject ,PA.Id AS id,PA.letterDocpath,PT.tenderNo ,PT.nitDocFileName
     FROM T_Project_AssignWork PA 
     INNER JOIN T_PROJECT_MASTER PM ON PA.projectId = PM.projectId
    INNER JOIN M_Proposal_Master PP ON PM.proposalId = PP.proposalId
    INNER JOIN T_PROJECT_TENDER PT ON PT.projectId = PA.projectId
    WHERE PA.id=P_Id;*/
     
     
     ELSEIF(P_Action='G') THEN -- Get Bidder Name & ID as Assign to and agreement amount
	 SELECT AA.BidderId,B.BidderName,AA.BidAmount,B.tenderId
      FROM T_PROJECT_TENDER_FINANCIAL_DTLS AS AA
     INNER JOIN T_PROJECT_TENDER_BIDDER_DTLS AS B ON AA.BidderId = B.BidderId
      INNER JOIN T_PROJECT_TENDER_FINANCIAL PTF ON PTF.ID=AA.ID 
      WHERE AA.DeletedFlag = 0 and PTF.deletedFlag=0 AND B.deletedFlag=0
      AND AA.WinnerStatus = 1
      and PTF.projectId=P_Id;
      
        
	ELSEIF(P_Action='H') THEN -- Get Bank Details  created By Akash kumar
	SELECT  PBP.instrumentType,PBP.pgAmount,PBP.bankName,DATE_FORMAT(PBP.validaityDate,'%d-%b-%Y')validaityDate,PBP.pgDocPath,PBP.pgDocPath FROM  T_Project_AssignWork TP 
	INNER JOIN T_PROJECT_BIDDER_PBGDTLS PBP ON PBP.assignWorkid = TP.id where PBP.assignWorkid=P_Id and  PBP.deletedFlag=0 and TP.deletedFlag=0;
        
END IF;

END ;;
