CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_BOQDetails`(
IN P_Action CHAR(30),
IN P_PMT VARCHAR(10),
IN P_projectId INT
)
BEGIN
     IF P_Action = 'A' THEN  # Get Project Name & Project Code

        IF P_PMT REGEXP '^[0-9]+$' THEN  
            SELECT projectId, projectCode 
            FROM T_PROJECT_MASTER 
            WHERE projectCode = P_PMT
            OR  projectCode LIKE CONCAT('%', P_PMT, '%');
        ELSE  
            SELECT projectId, projectName 
            FROM T_PROJECT_MASTER 
            WHERE projectName = P_PMT 
			OR projectName LIKE CONCAT('%', P_PMT, '%');
        END IF;
      ELSEIF  P_Action = 'B' THEN  # Get Project Name Details
       SELECT PM.projectId, PM.projectName 
       , AA.isMultipleProject as IsMultipleAAProject
       ,TS.boqPath as BoqTSDocPath
       ,PM.constructionArea as ConstructionArea
       ,PM.length
       ,TA.estimattedCost as EstimatedAmount
       , AA.aaEstimatedAmount as ApprovedAmount
       ,PM.projectCode as ProjectCode
            ,CAST((
        SELECT 
            GROUP_CONCAT(
                CONCAT(
                    PTC.ProjectCompnentId, '|', 
                    PTC.ComponentName, '|', 
					PTC.amount, '|', 
                    PTC.finExpenserpercent
                ) SEPARATOR '^'
            )
        FROM T_PROJECT_COMPONENT_DTLS PTC  -- Added alias here
        WHERE PTC.ProjectCompnentId = PC.Id AND PTC.deletedFlag = 0
    ) AS CHAR) AS ProjectComponentList
    ,PC.aaPartId as ProjectAAPartId
    ,PC.Id as ProjectComponentId
            FROM T_PROJECT_MASTER  PM
            left join T_PROPOSAL_TA_DETAILS TA on PM.proposalId=TA.proposalId and TA.deletedFlag=0
            left join T_PROPOSAL_AA_DETAILS AA on PM.proposalId=AA.proposalId and AA.deletedFlag=0
            left join T_PROPOSAL_TS_DETAILS TS on PM.proposalId=TS.proposalId and TS.deletedFlag=0
            left join T_PROJECT_COMPONENT PC on PC.projectId=PM.projectId and PC.deletedFlag=0
            WHERE PM.projectId=P_projectId;
     END IF; 
END ;;
