CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_CREATE_PROJECT`(
IN P_Action CHAR(50),
IN P_INT_ID INT
)
BEGIN
IF (P_Action = 'A')  # Proposal Details
 THEN
     SELECT PM.proposalId
      ,letterNo
      ,DATE_FORMAT(Now(), '%d-%b-%Y') as letterSubmittedDate
      ,subject as proposalSubject 
      , PM.letterDocPath as letterDocPath
      , (SELECT levelname FROM M_Adm_LevelDetails WHERE levelDetailId = PM.districtId)  as proposalLocation
      ,null areaOfConstruction
      ,null as length
      ,(Select clientName from M_Client_Master where M_Client_Master.deletedFlag=0 and clientId=PM.submittedByid)   as proposalSubmittedBy
      ,(Select VCHUSERNAME from M_POR_USER US where   US.INTUSERID=PM.createdBy)  as createdBy
      ,DATE_FORMAT(Now(), '%d-%b-%Y') as createdOn 
      ,(Select VCHUSERNAME from M_POR_USER where INTUSERID=TS.tsApprovedBy) as sanctionBy
      ,(case when TS.executionMode=1 then 'Department'
		when TS.executionMode=2 then 'Tender'
		else 'None' end )  as executionMode
       ,TA.estimattedCost  as amount
       ,TA.estimattedCost as estimatedAmount
      ,AA.aaEstimatedAmount as approvedAmount
      ,100.33 as sanctionedAmount
     
      ,TA.taLetterPath as letterDocPathTA
      ,AA.aaLetterPath as letterDocPathAA
      ,TS.tsLetterPath as letterDocPathTS
     
      
    #  FROM BUIDCO_devdb.M_Proposal_Master where deletedFlag=0 and proposalId=P_INT_ID;

from M_Proposal_Master PM 
left join T_PROPOSAL_TA_DETAILS TA on  TA.proposalId= PM.proposalId and TA.deletedFlag=0
left join T_PROPOSAL_AA_DETAILS AA on  AA.proposalId= PM.proposalId and AA.deletedFlag=0
left join T_PROPOSAL_TS_DETAILS TS on  TS.proposalId= PM.proposalId and TS.deletedFlag=0
where PM.deletedFlag=0 and PM.proposalId=P_INT_ID;
  ELSEIF (P_Action = 'B') # Created Project List
    THEN
       Select 
PM.projectId as ProjectId,
PM.proposalId as ProposalId,
Proposal.letterNo as ProposalLetterNo,
(Select clientName from M_Client_Master where deletedFlag=0 and clientId=Proposal.submittedByid)  as ProposalSubmittedBy,
Proposal.proposerName as ProposalProposedBy,
PM.projectCode ProjectCode,
PM.projectName as ProjectSubject,
DATE_FORMAT(PM.createdOn, '%d-%b-%Y') as ProjectSubmittedDate,
PM.constructionArea AreaOfConstruction,
PM.length as Length,
#TS.executionMode as ExecutionMode,
(case when TS.executionMode=1 then 'Department'
 when TS.executionMode=2 then 'Tender'
 else 'None' end ) as ExecutionMode,
AA.aaEstimatedAmount as SanctionedAmount,
PM.projectAmount as RevisedAmount,
'Not Started' as ProjectStage
 ,IFNULL(PM.isDataConfirmed,0) as IsProjectSubmitted

from T_PROJECT_MASTER as PM inner join M_Proposal_Master Proposal on  Proposal.proposalId= PM.proposalId
left join T_PROPOSAL_TA_DETAILS TA on  TA.proposalId= PM.proposalId and TA.deletedFlag=0
left join T_PROPOSAL_AA_DETAILS AA on  AA.proposalId= PM.proposalId and AA.deletedFlag=0
left join T_PROPOSAL_TS_DETAILS TS on  TS.proposalId= PM.proposalId and TS.deletedFlag=0

where PM.deletedFlag=0 and Proposal.deletedFlag=0  and Proposal.proposalStatus=4 and Proposal.statusId=3 order by PM.createdOn desc;

  ELSEIF (P_Action = 'C')# For Edit Project Details
    THEN
	SELECT PM.projectId as ProjectId,
Proposal.proposalId as ProposalId
      ,Proposal.letterNo as ProposalLetterNo
      ,DATE_FORMAT(Proposal.proposalLetterDate, '%d-%b-%Y') as ProposalLetterSubmittedDate
      ,Proposal.subject as proposalSubject 
      , Proposal.letterDocPath as ProposalLetterDocPath
      , (SELECT levelname FROM M_Adm_LevelDetails WHERE levelDetailId = Proposal.districtId) as ProposalLocation
      ,null as ProposalAreaOfConstruction
      ,null as ProposalLength
     ,(Select clientName from M_Client_Master where M_Client_Master.deletedFlag=0 and clientId=Proposal.submittedByid)  as ProposalSubmittedBy
     ,(Select VCHFULLNAME from M_POR_USER where INTUSERID=Proposal.createdBy) as ProposalCreatedBy
      ,DATE_FORMAT(Proposal.createdOn, '%d-%b-%Y') as ProposalCreatedOn
      ,(Select VCHUSERNAME from M_POR_USER where INTUSERID=TS.tsApprovedBy)as ProposalSanctionBy
      ,(case when TS.executionMode=1 then 'Department'
		when TS.executionMode=2 then 'Tender'
		else 'None' end )  as executionMode
       ,11110.23 as ProposalAmount
       ,TA.estimattedCost as ProposalEstimatedAmount
      ,AA.aaEstimatedAmount as ProposalApprovedAmount
      ,100.33 as ProposalSanctionedAmount
     
      ,TA.taLetterPath  as ProposalLetterDocPathTA
      ,AA.aaLetterPath as ProposalLetterDocPathAA
      ,TS.tsLetterPath  as ProposalLetterDocPathTS
      ,PM.projectName as ProjectName
      ,PM.projectAmount as ProjectAmount
      ,PM.constructionArea as ProjectAreaOfConstruction
      ,PM.length as ProjectLength
      ,IFNULL(PM.isDataConfirmed,0) as IsProjectSubmitted
      
       from T_PROJECT_MASTER as PM inner join M_Proposal_Master Proposal on  Proposal.proposalId= PM.proposalId
left join T_PROPOSAL_TA_DETAILS TA on  TA.proposalId= PM.proposalId and TA.deletedFlag=0
left join T_PROPOSAL_AA_DETAILS AA on  AA.proposalId= PM.proposalId and AA.deletedFlag=0
left join T_PROPOSAL_TS_DETAILS TS on  TS.proposalId= PM.proposalId and TS.deletedFlag=0

where PM.deletedFlag=0 and Proposal.deletedFlag=0 and PM.projectId=P_INT_ID;
        END IF;
END ;;
