    -- Edit (Single Record View)
    ELSEIF P_Action = 'C' THEN
        SELECT fyId, fyName, createdBy, createdOn, deletedFlag
        FROM M_Financial_Year 
        WHERE fyId = P_fyId;
	END IF;
