CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_LinkManagement`(
    IN P_ACTION VARCHAR(10),
    IN P_INTGLINKID INT,
    IN P_INTPLINKID INT
)
BEGIN
    IF P_ACTION = 'VG' THEN

        SELECT INTGLINKID as GLinkId, 
        NVCHGLINKNAME as GLinkName,
        VCHICONCLASS as LinkIcon,
        VCHGLINKURL as LinkURL,
        (select VCHFULLNAME from M_POR_USER where INTUSERID=INTCREATEDBY) as CreatedByName,
		DATE_FORMAT(DTMCREATEDON, '%d-%b-%Y') AS CreatedOn
        FROM M_ADM_GLOBALLINK
        WHERE BITSTATUS = 1;

    ELSEIF P_ACTION = 'VGS' THEN
       
        SELECT INTGLINKID as GLinkId, 
        NVCHGLINKNAME as GLinkName,
         VCHICONCLASS as LinkIcon,
        VCHGLINKURL as LinkURL,
        INTCREATEDBY,
		(select VCHFULLNAME from M_POR_USER where INTUSERID=INTCREATEDBY) as CreatedByName,
		DATE_FORMAT(DTMCREATEDON, '%d-%b-%Y') AS CreatedOn
        FROM M_ADM_GLOBALLINK
        WHERE INTGLINKID = P_INTGLINKID AND BITSTATUS = 1;

    ELSEIF P_ACTION = 'VP' THEN
        SELECT PL.INTPLINKID as PLinkId, PL.NVCHPLINKNAME as PLinkName,
               (SELECT GL.NVCHGLINKNAME
                FROM M_ADM_GLOBALLINK GL
                WHERE GL.INTGLINKID = PL.INTGLINKID) AS GLinkName,
               PL.VCHPLINKURL,
               (select VCHFULLNAME from M_POR_USER where INTUSERID=PL.INTCREATEDBY) as CreatedByName,
		       DATE_FORMAT(PL.DTMCREATEDON, '%d-%b-%Y') AS CreatedOn
        FROM M_ADM_PRIMARYLINK PL
        WHERE PL.BITSTATUS = 1;

        ELSEIF P_ACTION = 'VPS' THEN
        SELECT PL.INTPLINKID as PLinkId, 
        PL.NVCHPLINKNAME as PLinkName,
        PL.INTGLINKID as GLinkId,
        PL.VCHPLINKURL as LinkURL
        FROM M_ADM_PRIMARYLINK PL
        WHERE PL.INTPLINKID = P_INTPLINKID AND PL.BITSTATUS = 1;
        
        ELSEIF P_ACTION = 'BG' THEN
        SELECT INTGLINKID as GLinkId, 
        NVCHGLINKNAME as GLinkName FROM M_ADM_GLOBALLINK
        WHERE  BITSTATUS = 1;

    ELSE
        SELECT 'Invalid P_ACTION parameter' AS ErrorMessage;
    END IF;
END ;;
