CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_PROJECT_TENDER`(
IN P_Action CHAR(30),
IN P_PMT VARCHAR(10),
IN P_projectId INT
)
BEGIN
     IF P_Action = 'A' THEN  # Get Project Name & Project Code

        IF P_PMT REGEXP '^[0-9]+$' THEN  
            SELECT projectId, projectCode 
            FROM T_PROJECT_MASTER 
            WHERE projectCode = P_PMT
            OR  projectCode LIKE CONCAT('%', P_PMT, '%');
        ELSE  
            SELECT projectId, projectName 
            FROM T_PROJECT_MASTER 
            WHERE projectName = P_PMT 
			OR projectName LIKE CONCAT('%', P_PMT, '%');
        END IF;
      
      ELSEIF (P_Action = 'B') THEN  # Get Project Details By Project Id
	  Select -- PT.tenderId,
      PM.projectId, DATE_FORMAT(PM.createdOn,'%d-%b-%Y') as createdOn,PM.projectName,
      PM.projectCode,PM.constructionArea
      ,PM.length,PM.projectAmount from T_PROJECT_MASTER PM 
      -- INNER JOIN T_PROJECT_TENDER PT ON PM.projectId =PT.projectId
      where PM.projectId= P_projectId AND PM.deletedFlag=0;
      
      ELSEIF(P_Action = 'C')THEN # View Tender Details
      SELECT 
    PT.tenderId,
    DATE_FORMAT(PT.openingDate, '%d-%b-%Y') AS openingDate,
    DATE_FORMAT(PT.submissionDate, '%d-%b-%Y') AS submissionDate,
    PT.emdAmount,
    PT.processingFee,
    PT.nitDocFileName,
    PM.projectId,
    PM.projectName,
    PM.projectCode,
    PM.projectAmount,
    PM.constructionArea,
    PM.length,
    COALESCE(corrigendumData.corrigendumCount, 0) AS corrigendumCount,
    COALESCE(addendumData.addendumCount, 0) AS addendumCount
FROM T_PROJECT_TENDER PT
INNER JOIN T_PROJECT_MASTER PM ON PT.projectId = PM.projectId
LEFT JOIN (
    SELECT tenderId, COUNT(*) AS corrigendumCount
    FROM T_PROJECT_TENDER_CORRIGENDUM
    WHERE corrigendumType = 1 AND deletedFlag = 0
    GROUP BY tenderId
) corrigendumData ON PT.tenderId = corrigendumData.tenderId
LEFT JOIN (
    SELECT tenderId, COUNT(*) AS addendumCount
    FROM T_PROJECT_TENDER_CORRIGENDUM
    WHERE corrigendumType = 2 AND deletedFlag = 0
    GROUP BY tenderId
) addendumData ON PT.tenderId = addendumData.tenderId
WHERE PM.deletedFlag = 0 
AND PT.deletedFlag = 0;

       
       ELSEIF (P_Action = 'D')THEN  # Get Record for Edit the Record
    SELECT TM.tenderId,TM.projectId, TM.tenderNo,TM.tenderSubject,date_format(TM.publishedDate,'%d-%b-%Y')publishedDate,
    date_format(TM.submissionDate,'%d-%b-%Y')submissionDate,date_format(TM.openingDate,'%d-%b-%Y')openingDate,
    date_format(TM.submissionDate,'%d-%b-%Y')submissionDate, TM.emdAmount,TM.processingFee,
    TM.nitDocFileName, PM.projectCode,PM.projectName  FROM  T_PROJECT_TENDER TM JOIN 
    T_PROJECT_MASTER PM ON TM.projectId = PM.projectId WHERE TM.tenderId =P_PMT;
     END IF;
END ;;
