)
BEGIN
 IF(P_Action='A') THEN
  SELECT 
  PTP.Id,PTP.preBidexcelDoc,PTP.preBidpdfDoc,PTP.Id 
 ,PM.projectId,PM.projectCode,PM.projectName,PM.projectAmount,PM.constructionArea,PM.length
 ,PT.tenderId,date_format(PT.openingDate,'%d-%b-%Y')openingDate,PT.emdAmount,date_format(PT.submissionDate,'%d-%b-%Y')submissionDate,PT.nitDocFileName
